/****************************************************************************************
 회차 포커스 아웃될때 자동 0 입력 
 ****************************************************************************************/
function edExamOrder_OnKillFocus(obj) {
	gfn_ComponentOnKillFocus(obj);
    if (obj.Text != "") {
        obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));
    }
}


function lfn_ComponentOnKillFocus(obj)  {

	gfn_ComponentOnKillFocus(obj);
	obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3)); 
	lfn_Search();
}


function lfn_EnterNextFocus(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if (nChar == 13) {
        gfn_ComponentOnKillFocus(obj);
        obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));  
        lfn_Search();
    }  
}


function ds_ioExamJubsuList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {

	var cnt = 0; // 결강자수	
	for(i = 0; i < ds_ioExamSeatPrint.RowCount(); i++) {
        if (ds_ioExamSeatPrint.GetColumn(i, "SEL") == 1) {
            cnt += 1; // 시험접수인원 카운트
        } 
    }
    medExamList.Value = cnt;
}


function lfn_localExcel(obj) {  

    if (ds_ioExamJubsuList.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
    }
	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1208";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_ioExamJubsuList.GetRowCount();
		parValue += " sTitle=" + quote(gdExamJubsu.UserData);
	ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	
	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);
}


