#include "LIB::training.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

	 // 교육운영과와 관리자만 삭제권한 부여
	if (gds_oUserInfo.GetColumn(0, "PDEPTCD") == "0251" ||
		gds_oUserInfo.GetColumn(0, "ADMINGUBUN") == "SM") {
		
		btnDelete.Enable = true;
		
	} else {
		btnDelete.Enable = false;
		
	}



    // 공통 코드 조회
    gfn_SearchTrainingCourse("T", ds_oCourse, "Y", ""); // 직능 코드 조회	
	// 직능을 공공기관,건설현장소방안전관리자,업무대행감독자 만 적용 	
	ds_oCourse.Filter("CD == '20' || CD == '30'|| CD == '31'");	
	
    cbxCourse.Value = "30";
//	window.width = 1278;
	gdEvalList.Format = "COURSE30";


    gfn_SearchJibu(cbxJibu, "Y", "선택"); // 지부 코드 조회  
	radEvalGubun.Index = 0;
    edYear.Text = left(GetDate(), 4);
    
    edExamOrder.SetFocus();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdEvalList.Height = nCy - gdEvalList.Top - 4;
        gdEvalList.Width = nCx - gdEvalList.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
        
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
	edExamOrder.Text = iif(gfn_IsNull(edExamOrder.Text), "", lpad(edExamOrder.Text, "0", 3)); 
    /////////////////////  
        
    ds_ioEvaluationAnswerInput.ClearData(); // 데이타셋 초기화    
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchTraining0444EvaluationAnswerInput", false);
	
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioEvaluationAnswerInput=ds_oEvaluationAnswerInput"	// outDs
		,"EOMGGTMGNO=" + quote(cbxJibu.value)   //-- 지부
              +" EOYEAR=" + quote(edYear.text)  //-- 년도 
              +" EOTCCOURSECD=" + quote(cbxCourse.value)   //-- 직능              
              +" EOEXAMORDER=" + quote(edExamOrder.text) // -- 회차             
              // args 
		,"lfn_AfterSearchEvaluationJumsuInput"
		,false
	);
}


}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 결강자 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchEvaluationJumsuInput(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdEvalList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
    medListCnt.Value = ds_ioEvaluationAnswerInput.count();
    
    progMarkingCheck.Pos = 0;
}


