function lfn_Save(obj)
{

    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
 
    if (ds_ioEvaluationAnswerInput.GetCount() == 0) {
    
        gfn_Alert("조회된 내역이 없습니다.\n\n조회한 후 점수를 입력하세요!");
        
        return false;
    }
    
    var sInputCnt = 0;
	
	if (radEvalGubun.Value == "1") {	// 평가

		for (var i = 0; i < ds_ioEvaluationAnswerInput.GetCount(); i++ ) { 
		
			if ((ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT11") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT21") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT31") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT41") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT51") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT61") <> "미등록")) {
			
				sInputCnt = sInputCnt + 1;
			
			}
		}
	} else if (radEvalGubun.Value == "2") {	// 재평가1차
		
		for (var i = 0; i < ds_ioEvaluationAnswerInput.GetCount(); i++ ) { 
		
			if ((ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT12") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT22") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT32") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT42") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT52") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT62") <> "미등록")) {
			
				sInputCnt = sInputCnt + 1;
			
			}
		}
	} else if (radEvalGubun.Value == "3") {	// 재평가2차
		
		for (var i = 0; i < ds_ioEvaluationAnswerInput.GetCount(); i++ ) { 
		
			if ((ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT13") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT23") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT33") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT43") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT53") <> "미등록") || 
			   (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT63") <> "미등록")) {
			
				sInputCnt = sInputCnt + 1;
			
			}
		}
	}
	
	
	if (sInputCnt > 0 ) {
	
		gfn_Alert("답안이 등록된 건이 있습니다.\n\n재등록 하시겠습니까?\n\n"
		          + " ※ 재등록의 경우 해당회차 평가구분(평가,재평가)별로 삭제 후 재등록 됩니다.");
		
	}

    //gfn_Excel2Grid("gdExcel", "", 0); // 엑셀로딩팝업 호출
    gfn_Excel2Grid("gdNewExcel", "", 0); // 엑셀로딩팝업 호출
    if (ds_ioNewExcel.GetCount() < 1) { // 로딩할 데이타가 없는 경우
        gfn_Alert("등록할 답안이 없습니다.");
        return false;
    } else { // 저장 실행 ( 전체 삭제 후 전체 저장 )
//		lfn_AfterExcelCopy();
		lfn_AfterNewExcelCopy();
    }
    
}


/*
 *  Excel 그리드를 copy 함
 *  delete 상태값이 없는 DataSet을 이용해서 Table 를 Delete all 하기 위해서
 *  select  할때 처럼 함수를 사용하면서 delete 쿼리를 호출함
 */
 // Excel 그리드에서 키값을 추출해서 삭제하려면 아래 주석을 풀어서 사용하면 됨
function lfn_AfterExcelCopy() {

    	

    ds_ioExcelCopy.ClearData();

    for (var i = 0; i < ds_ioExcel.GetCount(); i++ ) { //ds_ioExcel.GetCount()
        ds_ioExcelCopy.AddRow();

        ds_ioExcelCopy.SetColumn(i, "EEAPEOMGNO",			ds_ioEvaluationAnswerInput.GetColumn(0, "EOMGNO"));
        
        ds_ioExcelCopy.SetColumn(i, "EEAPTCCOURSECD",		ds_ioEvaluationAnswerInput.GetColumn(0, "EOTCCOURSECD"));
        
        ds_ioExcelCopy.SetColumn(i, "EEAPEVALGUBUN",			radEvalGubun.Value);
        
        ds_ioExcelCopy.SetColumn(i, "EEAPJUBSUNONAME",		ds_ioExcel.GetColumn(i, "EEAPJUBSUNONAME"));

        ds_ioExcelCopy.SetColumn(i, "EEAPITEMNO1",			ds_ioExcel.GetColumn(i, "EEAPITEMNO1"));
        
