		ds_ioExcelCopy.SetColumn(i, "EEAPITEMNO3",			ds_ioExcel.GetColumn(i, "EEAPITEMNO3"));
        
        ds_ioExcelCopy.SetColumn(i, "EEAPANSWER3",			ds_ioExcel.GetColumn(i, "EEAPANSWER3"));
        
