/*
 *  Excel 그리드를 copy 함
 *  delete 상태값이 없는 DataSet을 이용해서 Table 를 Delete all 하기 위해서
 *  select  할때 처럼 함수를 사용하면서 delete 쿼리를 호출함
 */
 // Excel 그리드에서 키값을 추출해서 삭제하려면 아래 주석을 풀어서 사용하면 됨
 // 2021.09.28 개선
function lfn_AfterNewExcelCopy() {
    ds_ioExcelCopy.ClearData();
	var j = 0;
	var k = 0;
	// 1행부터 시작함. 0행은 제목행
    for (var i = 0; i < ds_ioNewExcel.GetCount(); i++ ) { //ds_ioExcel.GetCount()
    
		ds_ioExcelCopy.AddRow();
        
        if (ds_ioNewExcel.GetColumn(i, "EEAPJUBSUNONAME") == "User Name" ||
			ds_ioNewExcel.GetColumn(i, "EEAPJUBSUNONAME") == "사용자 이름") {	// 제목줄이면 
                
			k = i;		// 문항
			
        }
        
