// Excel 자료 전체 Insert
function lfn_AfterInsertAllExcel() {
    
	// 기존자료 삭제(해당회차, 평가구분)
	tit_clearActionInfo();
	tit_addSingleActionInfo("training:deleteTraining0444EvaluationAnswerPaper", "", 0, "");
	//서버 호출
	tit_callService(
		""
		,""
		,""	// inDs
		,""	// outDs
		,"EOMGNO=" + quote(ds_ioEvaluationAnswerInput.GetColumn(0, "EOMGNO")) // args
		
			+ " EVALGUBUN=" + quote(radEvalGubun.Value)   //-- 평가구분
		,""
		,false
		,false
		,true
	);
        
	
	// 엑셀자료 INSERT    tit_clearActionInfo();
    tit_addSaveActionInfo(
        "training:insertTraining0444EvaluationAnswerPaper"  // insert
        , ""    // update
        , ""    // delete
        , ""    // flag column 명
        , ""    // key sql id
        , 0 // key 증가 값
        , ""
        , 0
        , "N"   // 실행타입
    );
    
    // 서버 호출
    tit_callService(
        ""
        ,""
        ,"ds_iExcel=ds_ioExcelCopy:U"  // inDs

        ,"" // outDs
        ,""    // args
        ,"lfn_AfterSaveEvaluationAnswerPaper"
        ,false
        
        ,false

        ,true
    );
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveEvaluationAnswerPaper(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        if ( SubStr(strErrorMsg,0,9) == "ORA-02291") {
            gfn_Alert("로딩하려는 등록되지 않은 답안이 있습니다.\n답안 엑셀자료를 먼저 확인 후 로딩하세요.");
            return;
        } else {
            gfn_ErrorMsg(nErrorCode, strErrorMsg);
            return;
        }
    }
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    
    //ds_ioExcel.ClearData();	// 엑셀로딩자료 클리어
    ds_ioNewExcel.ClearData();	// 엑셀로딩자료 클리어
    lfn_Search(); // 조회
	
}


// 등록답안 삭제
function lfn_Delete(obj)
{
    if (ds_ioEvaluationAnswerInput.GetCount() == 0) {
    
        gfn_Alert("삭제할 내역이 없습니다.\n\n회차를 먼저 조회하세요!");
        
        return false;
    }
    
    var sDeleteCnt = 0;

    for (var i = 0; i < ds_ioEvaluationAnswerInput.GetCount(); i++ ) { 
    
		if ((ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT1") <> "미등록") ||
		    (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT1") <> "미등록") ||
		    (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT1") <> "미등록") ||
		    (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT1") <> "미등록") ||
		    (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT1") <> "미등록") ||
		    (ds_ioEvaluationAnswerInput.GetColumn(i, "SUBJECT1") <> "미등록")) {
	
			sDeleteCnt = sDeleteCnt + 1;
		
		}
		
	}
	
	if (sDeleteCnt == 0 ) {
	
        gfn_Alert("답안 입력된 건이 없습니다.\n\n삭제할 내역이 없습니다.");
        
        return false;	
	
	} else if (sDeleteCnt > 0 ) {
	
        if (!gfn_Confirm("답안 입력된 건이 있습니다.\n\n" + MSGW00030)) { // 삭제 하시겠습니까?
            return false;
        }
		
        tit_clearActionInfo();
        tit_addSingleActionInfo("training:deleteTraining0444EvaluationAnswerPaper", "", 0, "");
        //서버 호출
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,""	// outDs
            ,"EOMGNO=" + quote(ds_ioEvaluationAnswerInput.GetColumn(0, "EOMGNO")) 
            
			//	+ " EVALGUBUN=" + quote(radEvalGubun.value)   //-- 평가구분    // args 
            ,"lfn_AfterDeleteEvaluationAnswerPaper"
            ,false
            ,false
            ,true
        );
    }
	
}

function lfn_AfterDeleteEvaluationAnswerPaper(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_Alert("정상적으로 삭제되었습니다.");
    lfn_Search(); // 조회
}


// 초기화
function lfn_Init(obj)
{
	cbxCourse.Value = "30";
    
    edExamOrder.Text = "";
    ds_ioEvaluationAnswerInput.ClearData();
    
    medExamList.Text = "";
    
    progMarkingCheck.Pos = 0;
    
    edExamOrder.SetFocus();	
    
}


// 채점
function btnMarking_OnClick(obj)
{

    if (ds_ioEvaluationAnswerInput.GetCount() == 0) {
    
        gfn_Alert("조회된 내역이 없습니다.\n\n조회한 후 채점하세요!");
        
        return false;
    }

    if (!gfn_Confirm("채점을 하시겠습니까?")) {
        return;
    }
    progMarkingCheck.Pos = 0;
    
	
	lfn_GetCorrectAnswer();	// 정답지 조회
	
	lfn_AnswerSearch();	// 입력답안 조회
	
	lfn_SplitAnswerPaper();	// 답안 열단위 정렬
	
	lfn_MarkingAnswerPaper();	//	채점
	
	lfn_InsertAnswerPaperResult();	// 채점결과 INSERT
	
	lfn_InsertEvaluationResult();	// 평가결과 INSERT
	
	gfn_Alert("채점이 완료되었습니다.");

	lfn_Search();
}

/**
 * 정답지 로딩
 */
function lfn_GetCorrectAnswer() {
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }

    ds_oCorrectAnswer.ClearData(); // 데이타셋 초기화    
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchTraining0444EvaluationCorrectAnswer", false);
	
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oCorrectAnswer=ds_oCorrectAnswer"	// outDs
		,"EECPTCCOURSECD=" + quote(ds_ioEvaluationAnswerInput.GetColumn(0, "EOTCCOURSECD"))   //-- 과정
		
         + " EECPEVALGUBUN=" + quote(radEvalGubun.value)   //-- 평가구분
              // args 
		,""
		,false
		
        ,false

        ,true
	);
	
}

