/**
 * 답안 열단위 정렬
 */
function lfn_SplitAnswerPaper() {

    var seq = 1;
    ds_iAnswerPaper.ClearData();

    for (var i=0; i<ds_oAnswerPaper.GetRowCount(); i++) {

        var userAns;
        
        userAns = 30; // 직능구분에 따른 case는 추후 
        
        /*
        if (ds_oAnswerPaper.GetColumn(i, "APTCCOURSECD") == "20"
            || ds_oAnswerPaper.GetColumn(i, "APTCCOURSECD") == "21"
            || ds_oAnswerPaper.GetColumn(i, "APTCCOURSECD") == "22"
            
            || ds_oAnswerPaper.GetColumn(i, "APTCCOURSECD") == "23"
            
            || ds_oAnswerPaper.GetColumn(i, "APTCCOURSECD") == "50") {	// 공공,1급,2급,3급, 생활안전강사
            userAns = 30; //문항 마지막번호 고정
            
        } else if (ds_oAnswerPaper.GetColumn(i, "APTCCOURSECD") == "10") {	// 특급
            userAns = 100; //문항 마지막번호 고정
            
        } else if (ds_oAnswerPaper.GetColumn(i, "APTCCOURSECD") == "51") {	// 심폐소생술과정
            userAns = 20; //문항 마지막번호 고정
                        
        } else if (ds_oAnswerPaper.GetColumn(i, "APTCCOURSECD") == "24") {	// 위관
        
            userAns = 50;
            
        } else if (ds_oAnswerPaper.GetColumn(i, "APTCCOURSECD") == "28") {	// 운송자
        
            userAns = 50;
        
        } else if (ds_oAnswerPaper.GetColumn(i, "APTCCOURSECD") == "29") {	// 통합자
        
            userAns = 50;
            
        } else if (ds_oAnswerPaper.GetColumn(i, "APTCCOURSECD") == "27") {	// 운반자
        
            userAns = 50;
            
        } else {		// 
            userAns = 30;
            
        }
        */
        for (var j=1; j<userAns; j++) {
        
			// 문항 값이 있으면
			if (!gfn_isNull(ds_oAnswerPaper.GetColumn(i, "EEAPITEMNO"+j))) {

				var nCurrow = ds_iAnswerPaper.AddRow();
	
				ds_iAnswerPaper.SetColumn(nCurrow, "EEAPEOMGNO" , ds_oAnswerPaper.GetColumn(i, "EEAPEOMGNO"));
				
				ds_iAnswerPaper.SetColumn(nCurrow, "EEAPTCCOURSECD" , ds_oAnswerPaper.GetColumn(i, "EEAPTCCOURSECD"));
				
				ds_iAnswerPaper.SetColumn(nCurrow, "EEAPJUBSUNONAME" , ds_oAnswerPaper.GetColumn(i, "EEAPJUBSUNONAME"));
	
				ds_iAnswerPaper.SetColumn(nCurrow, "EEAPSUBJECTCD" , ds_oAnswerPaper.GetColumn(i, "EEAPSUBJECTCD"));
				
				ds_iAnswerPaper.SetColumn(nCurrow, "EEAPEVALGUBUN" , radEvalGubun.Value);

				ds_iAnswerPaper.SetColumn(nCurrow, "EEAPITEMNO" , replace(replace(replace(ds_oAnswerPaper.GetColumn(i, "EEAPITEMNO"+j), ds_oAnswerPaper.GetColumn(i, "ESCSUBJECTNM"), "")," ",""),"번", ""));
	
				ds_iAnswerPaper.SetColumn(nCurrow, "EEAPANSWER" , replace(ds_oAnswerPaper.GetColumn(i, "EEAPANSWER"+j),"번", ""));
	
				ds_iAnswerPaper.SetColumn(nCurrow, "EEAPANSWERYN" , "N");
	
				ds_iAnswerPaper.SetColumn(nCurrow, "EEAPITEMSCORE" , "0");
	
            }

        }
    }
//TRACE(ds_oAnswerPaper.SAVEXML());
//TRACE(ds_iAnswerPaper.SAVEXML());
}

// 채점 
function lfn_MarkingAnswerPaper(){
    var ls_AnswerCheck = "N"; // 정답여부
    var li_CorrectAnswerCnt = 0; // 정답여부
    var li_CorrectRow = 0; // 정답지 row
    var ls_AnswerPaperMark = "0"; // 답지 답
    var ls_CorrectPaperMark = "0"; // 정답
    
    SetWaitCursor(true);
    SetCapture();

    progMarkingCheck.Pos = 0;
    progMarkingCheck.Max = ds_iAnswerPaper.GetRowCount();
    
    for (var i=0; i<ds_iAnswerPaper.GetRowCount(); i++) {
    
        progMarkingCheck.StepIt();
    
        li_CorrectRow = ds_oCorrectAnswer.searchrow("EECPTCCOURSECD=" + quote(ds_iAnswerPaper.GetColumn(i, "EEAPTCCOURSECD"))
                + " && EECPSUBJECTCD=" + quote(ds_iAnswerPaper.GetColumn(i, "EEAPSUBJECTCD"))
                + " && EECPITEMNO=" + quote(ds_iAnswerPaper.GetColumn(i, "EEAPITEMNO"))
        );
        ls_AnswerPaperMark = ds_iAnswerPaper.GetColumn(i, "EEAPANSWER");
        ls_CorrectPaperMark = ds_oCorrectAnswer.GetColumn(li_CorrectRow, "EECPCORRECTANSWER");
        li_CorrectAnswerCnt = toNumber(ds_oCorrectAnswer.GetColumn(li_CorrectRow, "EECPCORRECTCNT"));

        if (ls_AnswerPaperMark = ls_CorrectPaperMark) { // 정답 비교

            ls_AnswerCheck = "Y";

        } else if (length(ls_CorrectPaperMark) > 1) { // 정답이 2개 이상일 때
        
            li_CorrectAnswerCnt = 0;
            
            for (var j=0; j<length(ls_AnswerPaperMark); j++) {
                for (var k=0; k<length(ls_CorrectPaperMark); k++) {
                    if (substr(ls_AnswerPaperMark, j, 1) = substr(ls_CorrectPaperMark, k, 1)) {
                        ls_AnswerCheck = "Y";
                        li_CorrectAnswerCnt++;
                        break;
                    } else {
                        ls_AnswerCheck = "N";
                    }
                }
                
                if (ls_AnswerCheck == "N") { // 틀린 답이 하나라도 있을 경우
                    li_CorrectAnswerCnt = 0;
                    break;
                }
            }
            
        } else {

            ls_AnswerCheck = "N";

        }
        
        if (ls_AnswerCheck == "Y" && li_CorrectAnswerCnt >= toNumber(ds_oCorrectAnswer.GetColumn(li_CorrectRow, "EECPCORRECTCNT"))) { // 답이 틀린게 없고, 정답 개수가 맞을 때
            ds_iAnswerPaper.SetColumn(i, "EEAPANSWERYN", ls_AnswerCheck);
            ds_iAnswerPaper.SetColumn(i, "EEAPITEMSCORE", ds_oCorrectAnswer.GetColumn(li_CorrectRow, "EECPITEMSCORE"));
        }
    }
    
    SetWaitCursor(false);

}


/**
 * 채점결과 insert
 */
function lfn_InsertAnswerPaperResult() {
	// 기존 등록결과 삭제 후
        tit_clearActionInfo();
        tit_addSingleActionInfo("training:deleteTraining0444EvaluationAnswerResult", "", 0, "");
        //서버 호출
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,""	// outDs
            ,"EOMGNO=" + quote(ds_ioEvaluationAnswerInput.GetColumn(0, "EOMGNO")) 
            
				+ " EVALGUBUN=" + quote(radEvalGubun.value)   //-- 평가구분    // args 
            ,""
            ,false
            ,false
            ,true
        );

//trace(ds_iAnswerPaper.savexml());//	채점결과 등록
    tit_clearActionInfo();
    tit_addSaveActionInfo(
        "training:insertTraining0444EvaluationAnswerPaperResult"  // insert
        , ""    // update
        , ""    // delete
        , ""    // flag column 명
        , ""    // key sql id
        , 0 // key 증가 값
        , ""
        , 0
        , "N"   // 실행타입
    );
    
    // 서버 호출
    tit_callService(
        ""
        ,""
        ,"ds_iAnswerPaper=ds_iAnswerPaper:U"  // inDs
        ,"" // outDs
        ,"" // args
        ,""
        ,false
        ,false

        ,true
    );
    
}




