              +" EOYEAR=" + quote(edYear.text)  //-- 년도
              +" EOTCCOURSECD=" + quote(cbxCourse.value)   //-- 직능              
              +" EOEXAMORDER=" + quote(edExamOrder.text) // -- 회차         // args 
		,""
		,false
		,false
		,true
	);
	//	평가결과 등록
    tit_clearActionInfo();
	tit_addSingleActionInfo("training:insertTraining0444EvaluationResult"
        , ""
        , 0
        , ""
    );
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,""	// outDs			
		,"EOMGGTMGNO=" + quote(cbxJibu.value)   //-- 지부
              +" EOYEAR=" + quote(edYear.text)  //-- 년도
              +" EOTCCOURSECD=" + quote(cbxCourse.value)   //-- 직능              
              +" EOEXAMORDER=" + quote(edExamOrder.text) // -- 회차         // args 
		,"lfn_AfterSaveTrainingEvaluation"
		,false
		,false
		,true
	);
}


/***********************************************************
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 ***********************************************************/
function lfn_AfterSaveTrainingEvaluation(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }   
}
 


