function lfn_localExcel(obj)
{  
    if (ds_ioEvaluationAnswerInput.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
    }
	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1119";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_ioEvaluationAnswerInput.GetRowCount();
		parValue += " sTitle=" + quote(gdEvalList.UserData);
	ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);
}


function gdEvalList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    if (ds_ioEvaluationAnswerInput.GetRowCount() == 0) {

        gfn_Alert("조회할 자료가 없습니다.");

        edExamOrder.SetFocus();

        return false;

    }

	var sArg = "sEjmgno=" + quote(ds_ioEvaluationAnswerInput.GetColumn(ds_ioEvaluationAnswerInput.currow, "EJMGNO"));

	Dialog("training::frmtraining0444PTrainingEvaluationAnswerView.xml", sArg, 800, 500, "", -1, -1);

}

