/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    ms_FormAuth = "W";

    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }
    
    // 로딩 시 시험명 리스트 출력
    lfn_SearchExamOrderList();  
    //lfn_Search();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리

}

function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
        gdExamJubsu.Height = nCy - gdExamJubsu.Top - 4;
        gdExamJubsu.Width = nCx - gdExamJubsu.Left - 4;
    }
}

/*
 *  시험명 리스트
 */
function lfn_SearchExamOrderList() {
    ldb.Sync = true;
    ds_oExamOrder.ClearData(); // 데이타셋 초기화
    ds_oExamOrder.Load("SvcLdb::searchExamOrderList.xml");
    ldb.Sync = false;
}

function lfn_AfterSearchExamOrder(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }    
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oExamJubsuPersonList.ClearData(); // 데이타셋 초기화
    ldb.Sync = true;
    transaction("select",
                "SvcLdb::searchExamJubsuListPass.xml",
                "",
                "ds_oExamJubsuPersonList=output",
                "EOMGNO=" + ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "EOMGNO")
                + " EOHJUBSUNO='" + edSeq.Text + "'"
                + " EJPERSONNM='" + edCustomerNm.Text + "'",
                ""); 
    ldb.Sync = false;
    medJubsuCnt.Value = ds_oExamJubsuPersonList.rowcount;
}

/*
 * 기능 : 출력
 */
function lfn_Print(obj){
    // to do Validation Check
    gfn_CallReportPopup("frmtraining0280R02PassRegister.rex"
                      , "ds_oExamJubsuPersonList"
                      , "sParam_Title=" + quote("_수료대장출력")
                       + "^Jibu=" + quote(ds_oExamJubsuPersonList.GetColumn(ds_oExamJubsuPersonList.currow, "GTTEAMNM"))) ;
}

