/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdExamJubsu, 1); // 멀티 정렬을 실행한다.
}

function gdExamJubsu_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	var lobj_Dataset = object(obj.BindDataset);
	if (nCell == 0) { // 선택
        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우
            obj.SetCellProp("head", nCell, "text", 0);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "0");
            }
        } else { // 전체가 이미 선택되지 않은 경우
            obj.SetCellProp("head", nCell, "text", 1);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "1");
            }
        }
	} else { // 헤드 정렬
        lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex);
	}
}

/**
 *  그리드 클릭시 사용 함수 
 */ 
function ds_oExamJubsuPersonList_OnRowPosChanged(obj,nOldRow,nRow) {
    if (obj.GetRowType(nRow) <> "insert" && nRow <> -1) {
        edSeq.Text = obj.GetColumn(nRow, "JUBSUNO");
        medSeatNo.Text = obj.GetColumn(nRow, "EOHEXAMSEATNO");
        edCustomerNm.Text = obj.GetColumn(nRow, "TJPERSONNM");
        medBIRTHDAY.Text = obj.GetColumn(nRow, "TPBIRTHDAY");
        medZipCode.Text = obj.GetColumn(nRow, "TJZIPCD");
        edAddr1.Text = obj.GetColumn(nRow, "ADDR1");
        edAddr2.Text = obj.GetColumn(nRow, "ADDR2");
        edTel.Text = obj.GetColumn(nRow, "TPTEL");
        edHpTel.Text = obj.GetColumn(nRow, "TPHPTEL");
        edPassNo.Text = obj.GetColumn(nRow, "TPPASSNO");        
        calStartDt.Value = obj.GetColumn(nRow, "TPPASSDATE");
    }
}

function gdExamJubsu_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    ds_oExamJubsuPersonList_OnRowPosChanged(lobj_Dataset,nRow,nRow);
}

function btnNext_OnClick(obj) {
    var ls_MenuId = "0000000082";
    var ls_MenuTitle = "정답지편집";
    var ls_MenuUrl = "training::frmtraining0510MExamCorrectAnswerPaperModify.xml";
    
    gs_SystemGubun = "training";
    gs_SystemMenuId = "0000000003";
    global.mnuLeft.tabMenu.tabpgMenu.Text = "강습관리";

    global.mnuTop.tabTopMenu.TabIndex = gds_oTopMenu.FindRow("MSYSGUBUN", gs_SystemGubun);
    gfn_OpenChildWindow(ls_MenuUrl, ls_MenuTitle, ls_MenuId);
}

function btnToExcel_OnClick(obj) {
	if ( ds_oExamJubsuPersonList.GetTotalRowCount() > 0 ) {
        //lfn_Excel();		// 개인정보 다운로드 사유 등록
		var parValue = "sCode=1213";	// COMMON.PERSONALDOWNLOADH
			parValue += " sRowCnt=" + ds_oExamJubsuPersonList.GetRowCount();
			parValue += " sTitle=" + quote(gdExamJubsu.UserData);
		ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	
	
		if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
			return false;
		}
	
        var mobj_Export;
        mobj_Export = CreateExportObject();
        mobj_Export.ExportType = "Excel";
        mobj_Export.ExportFileName = "시험응시자목록.xls";
        mobj_Export.ActiveSheetName = gdExamJubsu.UserData;
        mobj_Export.MakeEmptyFileWhenNotExist = false;    
        mobj_Export.AddExportGrid(gdExamJubsu.UserData + "!" + "A1", gdExamJubsu, true, false);   
        mobj_Export.Export();
        mobj_Export.Close();
        mobj_Export = null;
	} else {
        gfn_Alert("응시자 목록 가져오기를 먼저 하세요.");
	}
}

function ds_oExamOrder_OnRowPosChanged(obj,nOldRow,nRow)
{
    lfn_Cancel();
	lfn_Search();
}

function edExamOrder_OnKillFocus(obj) {
	gfn_ComponentOnKillFocus(obj);
    obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 6));
}

function edExamOrderEnd_EnterSearch(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) { // Enter Key
        edSeq.Text = iif(gfn_IsNull(edSeq.Text), "", lpad(edSeq.Text, "0", 6));
        lfn_Search();
    }
}
