function ds_oExamOrder_OnRowPosChanged(obj,nOldRow,nRow)

{
	// 22.12 공공,업무대행,건설현장도 추가 
	if (ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "21"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "22"
        
        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "23"
        
        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "20"
        
        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "30"
        
        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "31"
        
        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "50"
        
        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "51"
        
        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "53") {

        //cbxExamRepeat.Enable = true;

        //cbxExamRepeat.Value = "1";

        lbExamRepeat.Visible = false;

        cbxExamRepeat.Visible = false;

	} else {

        cbxExamRepeat.Visible = true;

        lbExamRepeat.Visible = true;

        cbxExamRepeat.Value = "1";        

	}

}

// OMR 입력(Scan방식)
function btnScan_OnClick(obj)
{
	// Image 폴더가 있으면 답안이미지파일 삭제
	DirRtn = ext_GetFileList("C:\\KFSA\\kems\\training\\local\\kems\\localsvc","D");
	
	if(indexOf(DirRtn, "Image") >= 0) {

		var fileRtn = ext_GetFileList("C:\\KFSA\\kems\\training\\local\\kems\\localsvc\\Image\\정상용지","F");
		
		var i = 0;
		while (indexOf(fileRtn, fileRtn[i]) >= 0) {
			OmrLocalDelete.Delete("C:\\KFSA\\kems\\training\\local\\kems\\localsvc\\Image\\정상용지\\"+fileRtn[i]);
			i++;
		}
	}

	// 윈도우 bit체크(32비트인지 64비트인지)
	var sPath;

	var rtn = ext_RegGetValue_win7("HKEY_LOCAL_MACHINE", "SOFTWARE\\WOW6432Node", "", "S");

	// 32비트
	if( !gfn_isNull(rtn) || rtn != "") {
		sPath = "C:\\Program Files\\";
	} else {
		sPath = "C:\\Program Files (x86)\\";
	}

	if (ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "10") {
		// 2017.06.28 특급도 Scan방식 사용
		//ExecProc("C:\\KFSA\\kems\\training\\local\\kems\\localsvc\\KEMSOMR.exe", "0");
		ExecProc(sPath+"OMRScan\\소방안전협회\\OMRScan_KFSA.exe 1 " + gds_oUserInfo.GetColumn(0, "PSABUN"));
		
		// 22.12 공공,업무대행,건설현장도 추가 
	} else if (ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "21"

			|| ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "22"
			
			|| ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "23"
			
			|| ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "20"
			
			|| ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "30"
			
			|| ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "31"
			
			|| ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "50"
			
			|| ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "51"
			
			|| ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "53") {
		
		ExecProc(sPath+"OMRScan\\소방안전협회\\OMRScan_KFSA.exe 1 " + gds_oUserInfo.GetColumn(0, "PSABUN"));

	} else {

		ExecProc(sPath+"OMRScan\\소방안전협회\\OMRScan_KFSA.exe " + cbxExamRepeat.Value+ " "+ gds_oUserInfo.GetColumn(0, "PSABUN"));

	}
}
