var ms_ExamMgno = "";
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회
    gfn_SearchTrainingCourse("E", ds_oCourse, "Y", "전체"); // 직능 코드 조회
    cbxCourse.Value = "22";
    gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회

    edExamYear.Text = left(GetDate(), 4);
    edExamOrder.SetFocus();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdExamRoom.Height = nCy - gdExamRoom.Top - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_ioExamRoom.ClearData(); // 데이타셋 초기화
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",년도=" + quote(edExamYear.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",회차=" + quote(edExamOrder.Text)
                + ",종별=" + quote(cbxNumType.Text)
                + ",시험장=" + quote(edExamRoom.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
    medTotalCnt.Value = "";
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchExamRoom", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioExamRoom=ds_oExamRoom"	// outDs
		,"EOMGJIBUCD=" + quote(cbxJibu.Value)
            + " EOYEAR=" + quote(edExamYear.Text)
            + " TCCOURSECD=" + quote(cbxCourse.Value)
            + " EOEXAMORDER=" + quote(edExamOrder.Text)
            //+ " ERNUMTYPE=" + quote(cbxNumType.Value)
            //+ " EREXAMROOM=" + quote(edExamRoom.Text) // args 
		,"lfn_AfterSearchExamRoom"
		,false
	);
}

/*
 * 기능 : 추가
 */
function lfn_Input(obj) {
	if (!gfn_Confirm(MSGI00070)) { // 신규 추가 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.
        return false;
	}

	mb_IsNewData = true; // 저장버튼이 클릭 설정
    lfn_Cancel(); // 값 초기화
    edExamOrder.SetFocus();

    // 기본값 설정
}

/*
 * 기능 : 삭제
 */
function lfn_Delete(obj) {
    if (ds_ioExamRoom.currow < 0) {
        gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
        return false;
    }

	if (!gfn_Confirm(MSGW00030)) { // 정말로 삭제 하시겠습니까?
        return false;
	}
	
	lfn_SaveModifyHistory("D"); // 변경이력을 저장한다.

    ds_ioExamRoom.DeleteRow(ds_ioExamRoom.currow); // 선택된 ROW를 삭제한다.

    gfn_FormInit(this); // 입력란 초기화

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""	// insert
		, ""	// update
		, "training:deleteExamRoom"	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iExamRoom=ds_ioExamRoom:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterSaveExamRoom"
		,false
	);
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }    

    if (mb_IsNewData || ds_ioExamRoom.GetCount() == 0) { // 신규 저장
        if (gfn_IsNull(ms_ExamMgno)) {
            gfn_Alert("시험일정이 없습니다.\n시험일정을 먼저 확인하십시오."); // "시험을 먼저 선택해 주십시오.";
            edExamOrder.SetFocus();
            return false;
        }
        // 키 중복 체크
        lfn_SearchB();
        if (!gfn_Confirm(MSGW00020)) { // 정말로 추가 저장 하시겠습니까?
            return false;
        } 
        
        for (var i=0;i<=ds_ioExamRoomSearch.GetRowCount();i++) {
            if (ds_ioExamRoomSearch.GetColumn(i, "EOMGNO") == ms_ExamMgno
                && ds_ioExamRoomSearch.GetColumn(i, "EREXAMROOM") == edExamRoom.Text) {
                gfn_Alert("중복된 교육장이 있습니다.");
                lfn_Search();
                return false;
            }
        }
        /*
        for (var i=0;i<=ds_ioExamRoomSearch.GetRowCount();i++) {
            if (ds_ioExamRoomSearch.GetColumn(i, "EREXAMROOM") == edExamRoom.Text) {
                gfn_Alert("중복된 교육장이 있습니다.");
                lfn_Search();
                return false;
            }
        }         
        */
        // 입력된 값을 그리드로 복사한다.
        ds_ioExamRoom.InsertRow(0);
        ds_ioExamRoom.SetColumn(0, "EOMGNO", ms_ExamMgno);
        ds_ioExamRoom.SetColumn(0, "ERNUMTYPE", cbxNumType.Value);
        ds_ioExamRoom.SetColumn(0, "EREXAMROOM", edExamRoom.Text);
        ds_ioExamRoom.SetColumn(0, "USE_YN", chkUseYn.Value);
        
        ds_ioExamRoom.SetColumn(0, "ERCHASUGUBUN", cbxChasuGubun.Value);
        
        lfn_SaveModifyHistory("I"); // 변경이력을 저장한다.
    } else { // 수정 저장
        if (ds_ioExamRoom.GetColumn(ds_ioExamRoom.currow, "EOMGNO") != ms_ExamMgno) {
            gfn_Alert("년도 직능 회차는 수정이 불가능합니다.");
            cbxCourse.SetFocus();
            return false;
        }
        
        if (!gfn_Confirm(MSGW00040)) { // 정말로 수정 저장 하시겠습니까?
            return false;
        }        

        // 입력된 값을 그리드로 복사한다.
        ds_ioExamRoom.SetColumn(ds_ioExamRoom.currow, "EOMGNO", ms_ExamMgno);
        ds_ioExamRoom.SetColumn(ds_ioExamRoom.currow, "ERNUMTYPE", cbxNumType.Value);
        ds_ioExamRoom.SetColumn(ds_ioExamRoom.currow, "EREXAMROOM", edExamRoom.Text);
        ds_ioExamRoom.SetColumn(ds_ioExamRoom.currow, "USE_YN", chkUseYn.Value);
        
        ds_ioExamRoom.SetColumn(ds_ioExamRoom.currow, "ERCHASUGUBUN", cbxChasuGubun.Value);
        
        lfn_SaveModifyHistory("U"); // 변경이력을 저장한다.
    }

    gfn_FormInit(this); // 입력란 초기화

    tit_clearActionInfo();
    tit_addSaveActionInfo(
        "training:insertExamRoom"	// insert
        , "training:updateExamRoom"	// update
        , ""	// delete
        , ""	// flag column 명
        , ""	// key sql id
        , 0	// key 증가 값
        , "" 
        , 0 
        , "N"	// 실행타입
    );
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iExamRoom=ds_ioExamRoom:U"	// inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterSaveExamRoom"
        ,false
    );
}

