/*
 * 기능 : 시험장 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchExamRoom(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    medTotalCnt.Value = ds_ioExamRoom.rowcount;
    gfn_SearchMessage(gdExamRoom); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

// 교육장 중복체크후
function lfn_AfterSearchExamRoomSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }   
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveExamRoom(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 시험장 조회
}

/*
 * 기능 : 시험회차관리번호를 조회한다.
 * parameter : 없음
 * return : 없음
 */
function lfn_SearchExamOrderMgno() {
    if ((gfn_IsNull(cbxJibu.Value)
            || gfn_IsNull(cbxCourse.Value)
            || gfn_IsNull(edExamYear.Text)
            || gfn_IsNull(edExamOrder.Text))) {
        //gfn_Alert("지부, 직능, 년도, 회차를 입력해 주십시오.");
        return false;
    }

    ds_oExamOrderMgno.ClearData(); // 데이타셋 초기화
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edExamYear.Text)
                + ",회차=" + quote(edExamOrder.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , "회차관리번호검색"
        , ls_HistoryArg
    );

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchExamOrderMgno", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oExamOrderMgno=ds_oExamOrderMgno"	// outDs
		,"EOMGTMGNO=" + quote(cbxJibu.Value)
            + " EOTCCOURSECD=" + quote(cbxCourse.Value)
            + " EOYEAR=" + quote(edExamYear.Text)
            + " EOEXAMORDER=" + quote(lpad(edExamOrder.Text, "0", 3))
		,"lfn_AfterSearchExamOrderMgno"
		,false
	);
}

/*
 * 기능 :  시험회차관리번호 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchExamOrderMgno(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    if (ds_oExamOrderMgno.GetCount() == 1) {
        ms_ExamMgno = ds_oExamOrderMgno.GetColumn(0, "EOMGNO");
    } else {
        ms_ExamMgno = "";
    }
}

