function ds_ioExamRoom_OnRowPosChanged(obj,nOldRow,nRow)
{
    // 그리드의 값을 입력란에 복사한다. 추가상태인 경우 복사 후 저장을 하기 위해 제외한다.
    if (obj.GetRowType(nRow) <> "insert" && nRow <> -1) {
        cbxJibu.Value = obj.GetColumn(nRow, "EOMGJIBUCD");
        edExamYear.Text = obj.GetColumn(nRow, "EOYEAR");
        cbxCourse.Value = obj.GetColumn(nRow, "TCCOURSECD");
        edExamOrder.Text = obj.GetColumn(nRow, "EOEXAMORDER");
        cbxNumType.Value = obj.GetColumn(nRow, "ERNUMTYPE");
        edExamRoom.Text = obj.GetColumn(nRow, "EREXAMROOM");
        chkUseYn.Value = obj.GetColumn(nRow, "USE_YN");
        
        cbxChasuGubun.Value = obj.GetColumn(nRow, "ERCHASUGUBUN");
        
        gfn_MakeKeyMark(this); // 키값 수정 금지 설정
	}
	mb_IsNewData = false; // 저장버튼이 클릭 초기화
}

function gdExamRoom_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    var lobj_Dataset = object(obj.BindDataset);
	ds_ioExamRoom_OnRowPosChanged(lobj_Dataset,nRow,nRow);
}

