function lfn_Search(obj)
{
    if (!gfn_FormValidate(this)) { // 조회 Validation Check
        return false;
    }
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edExamYear.Text)
                + ",회차=" + quote(edExamOrder.Text)
                + ",정렬방법=" + quote(radSort.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
    
    // 실무능력평가표
    ds_oEvaluation.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchExam0633PracticeEvaluation", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oEvaluation=ds_oEvaluation"	// outDs
		,"EOMGGTMGNO=" + quote(cbxJibu.Value)
            + " EOTCCOURSECD=" + quote(cbxCourse.Value)
            + " EOYEAR=" + quote(edExamYear.Text)
            + " EOEXAMORDER=" + quote(edExamOrder.Text)
            + " SORT=" + quote(radSort.Value)// args 
		,"lfn_AfterSearchEvaluation"
		,false
	);	
}


/*
 * 기능 : 실무능력평가표 출력
 */
function lfn_Print(obj)
{    
    if (ds_oEvaluation.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
    // 출력이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edExamYear.Text)
                + ",회차=" + quote(edExamOrder.Text)
                + ",정렬방법=" + quote(radSort.Text);
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , "심폐소생술 실습평가표출력"
        , ls_HistoryArg
    );
    
	
	var sReportName = "frmtraining0633R01PracticeEvaluation.rex";
	
	
	
    gfn_CallReportPopup(sReportName
                      , "ds_oEvaluation"//||ds_oTrainingAttendencePoto
                      , "");
}

