 * 기능 : 조회
 */
function lfn_Search(obj) {
        
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
	edExamOrder.Text = iif(gfn_IsNull(edExamOrder.Text), "", lpad(edExamOrder.Text, "0", 3)); 
    /////////////////////  
        
    ds_ioEvaluationJumsuInput.ClearData(); // 데이타셋 초기화    
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchExam0634EvaluationJumsuInput", false);
	
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioEvaluationJumsuInput=ds_oEvaluationJumsuInput"	// outDs
		,"EOMGGTMGNO=" + quote(cbxJibu.value)   //-- 지부
              +" EOYEAR=" + quote(edExamYear.text)  //-- 년도 
              +" EOTCCOURSECD=" + quote(cbxCourse.value)   //-- 직능              
              +" EOEXAMORDER=" + quote(edExamOrder.text) // -- 회차             
              // args 
		,"lfn_AfterSearchEvaluationJumsuInput"
		,false
	);
}


}
