function lfn_Save(obj)
{

    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
 
    if (ds_ioEvaluationJumsuInput.GetCount() == 0) {
    
        gfn_Alert("조회된 내역이 없습니다.\n\n조회한 후 점수를 입력하세요!");
        
        return false;
    }
    
    var sInputCnt1 = 0;
    var sInputCnt2 = 0;

    for (var i = 0; i < ds_ioEvaluationJumsuInput.GetCount(); i++ ) { 
    
		if (ds_ioEvaluationJumsuInput.GetColumn(i, "ER1CHASUSCORE") == "입력안됨") {	//이론점수
		
			sInputCnt1 = sInputCnt1 + 1;
		
		}
    
		if (ds_ioEvaluationJumsuInput.GetColumn(i, "EISITEMSCORE") <> "입력안됨") {		//실기점수
		
			sInputCnt2 = sInputCnt2 + 1;
		
		}
		
	}
	
	if (sInputCnt1 > 0 ) {
	
		gfn_Alert("이론점수가 입력안된 건이 " + sInputCnt1 + "건 있습니다.\n\n이론점수 입력 후 실기점수를 입력하세요!");
		
		return false;
		
	}
	
	
	if (sInputCnt2 > 0 ) {
	
		gfn_Alert("실기점수가 입력된 건이 " + sInputCnt2 + "건 있습니다.\n\n확인 후 입력하세요!");
		
	}
    
    gfn_Excel2Grid("gdExcel", "", 0); // 엑셀로딩팝업 호출
    if (ds_ioExcel.GetCount() < 1) { // 로딩할 데이타가 없는 경우
        gfn_Alert("등록할 실기평가답안 점수가 없습니다.");
        return false;
    } else { // 저장 실행 ( 전체 삭제 후 전체 저장 )
        lfn_AfterExcelCopy();
    }
    
}


/*
 *  Excel 그리드를 copy 함
 *  delete 상태값이 없는 DataSet을 이용해서 Table 를 Delete all 하기 위해서
 *  select  할때 처럼 함수를 사용하면서 delete 쿼리를 호출함
 */
 // Excel 그리드에서 키값을 추출해서 삭제하려면 아래 주석을 풀어서 사용하면 됨
function lfn_AfterExcelCopy() {

    var ln_RowNo = "";
    var ln_EPCOURSECD_Temp = Array(3);
    var ln_EPNUMTYPE_Temp = Array(3);
    var ln_EPALPHATYPE_Temp = Array(3);
    var ln_Temp0 = "";
    var ln_Temp1 = "";
	var ln_OldAPLEXAMNO = "";
	
	var ln_1Score = 0;
	
	var ln_2Score = 0;
	
	var ln_ScoreSum = 0;
	
	var ln_CurrRow = "";

	var ls_PassScore = 60; //합격 점수
	
	var ls_SubjectPassScore = 40; //과락 점수

    ds_ioExcelCopy.ClearData();
	ds_ioExamResult.ClearData();
	
    //ds_ioExamResult.AddRow();
    
    var ln_ExamResultRowCnt = 0;
    
    for (var i = 0; i < ds_ioExcel.GetCount(); i++ ) { //ds_ioExcel.GetCount()

        ds_ioExcelCopy.AddRow();

        ds_ioExcelCopy.SetColumn(i, "EISEOMGNO",			ds_ioEvaluationJumsuInput.GetColumn(0, "EOMGNO"));
        ds_ioExcelCopy.SetColumn(i, "EISAPLEXAMNO",			ds_ioExcel.GetColumn(i, "EISAPLEXAMNO"));
        ds_ioExcelCopy.SetColumn(i, "EISPERSONNM",			ds_ioExcel.GetColumn(i, "EISPERSONNM"));
        ds_ioExcelCopy.SetColumn(i, "EISSUBJECTGUBUN",		"1");
        
        ds_ioExcelCopy.SetColumn(i, "EISITEMSCORE",			Round(ds_ioExcel.GetColumn(i, "EISITEMSCORE"), 1));
        
		// 점수등록
		ln_1Score = ds_ioEvaluationJumsuInput.GetColumn(i, "ER1CHASUSCORE");	// 이론점수
				
		ds_ioExamResult.AddRow();
		
		ln_2Score = toNumber(Round(ds_ioExcel.GetColumn(i, "EISITEMSCORE"), 1)); // 실기점수
		
		ln_ScoreSum = (toNumber(ln_1Score)+toNumber(ln_2Score))/2;	// 이론점수+실기점수
		
		ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "EREJMGNO", ds_ioEvaluationJumsuInput.GetColumn(i, "EJMGNO"));
		
		ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "ERAPLEXAMNO", ds_ioExcel.GetColumn(i, "EISAPLEXAMNO"));
		
		ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "ERPASSDATE", ds_ioEvaluationJumsuInput.GetColumn(i, "EOEXAMDATE"));
		
		ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "ERSCORE", ln_ScoreSum);	// 이론+ 실기점수
		
		ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "ER2SCORE", ln_2Score);	// 실기점수


		// 합격여부 계산
		// 이론+실기 60점 이상, 이론,실기별 40점 미만 과락
		if (toNumber(ln_ScoreSum) >= toNumber(ls_PassScore)) {	// 이론+실기 >= 60
			if (toNumber(ln_1Score) >= toNumber(ls_SubjectPassScore) && toNumber(ln_2Score) >= toNumber(ls_SubjectPassScore)) {	//이론>=40 실기>=40
			
				ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "ERPASSYN", "Y");	// 합격
				ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "EJPASSYN", "Y");	// 합격
				
			} else {
			
				ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "ERPASSYN", "N");	// 불합격
				ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "EJPASSYN", "N");	// 불합격
				ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "ERPASSDATE", "");
			}
			
		} else {
		
			ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "ERPASSYN", "N");	// 불합격
			ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "EJPASSYN", "N");	// 불합격
			ds_ioExamResult.SetColumn(ln_ExamResultRowCnt, "ERPASSDATE", "");
			
		}
			
		ln_ExamResultRowCnt = ln_ExamResultRowCnt + 1;

		// 점수 초기화
		ln_1Score = 0;
		ln_2Score = 0;
		ln_ScoreSum = 0;
    }
	// 정렬하기
	ds_ioExamResult.Sort("ERSCORE:D");
	
	// 석차 계산
	for (var j=0; j<ds_ioExamResult.GetRowCount(); j++) {
		if (ds_ioExamResult.GetColumn(j, "ERSCORE") == ds_ioExamResult.GetColumn(j - 1, "ERSCORE")) {
			ds_ioExamResult.SetColumn(j, "ERRANK", ds_ioExamResult.GetColumn(j - 1, "ERRANK"));
		} else {
			ds_ioExamResult.SetColumn(j, "ERRANK", j + 1);
		}
	}
	
    lfn_AfterInsertAllExcel();

}


// Excel 자료 전체 Insert
function lfn_AfterInsertAllExcel() {
    tit_clearActionInfo();
    tit_addSaveActionInfo(
        "training:insertExam2TimeAnswerPaper"  // insert
        , ""    // update
        , ""    // delete
        , ""    // flag column 명
        , ""    // key sql id
        , 0 // key 증가 값
        , ""
        , 0
        , "N"   // 실행타입
    );
    
	tit_addMultiActionInfo("training:updateExam0634ExamResult", "", 0, "", "N");
	tit_addMultiActionInfo("training:updateExam0634ExamJubsu", "", 0, "", "N");

    // 서버 호출
    tit_callService(
        ""
        ,""
        ,"ds_iExcel=ds_ioExcelCopy:U ds_ioExamResult=ds_ioExamResult ds_ioExamResult=ds_ioExamResult"  // inDs
        ,"" // outDs
        ,"" // args
        ,"lfn_AfterSaveExamJubsu2Score"
        ,false
    );

}


