#include "LIB::training.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회
    gfn_SearchTrainingCourse("T", ds_oCourse, "Y", "전체"); // 직능 코드 조회
    ds_oCourse.Filter("CD=='21'||CD=='22'||CD=='23'");	// 1급 2급 3급 소관만
    cbxCourseSource.Value = "22";
    cbxCourseTarget.Value = "22";
    gfn_SearchJibu(cbxJibuSource, "Y", "선택"); // 지부 코드 조회
    //gfn_SearchJibu(cbxJibuTarget, "Y", "전체"); // 지부 코드 조회
    cbxJibuTarget.Value = cbxJibuSource.Value;
    chkJubsuProcGubun.Value = "Y";
    chkJubsuProcGubun_OnClick();
    calSunapDate.Value = GetDate();
    edExamYearSource.Text = left(GetDate(), 4);
    edExamYearTarget.Text = left(GetDate(), 4);
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdTrainingExam.Height = nCy - gdTrainingExam.Top - 4;
        gdTrainingExam.Width = nCx - gdTrainingExam.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    if (gfn_IsNull(cbxJibuSource.Value)) {
        gfn_Alert("불합격시험의 지부를 선택하십시오.");
        cbxJibuSource.SetFocus();
        return false;
    }
    if (gfn_IsNull(edExamYearSource.Text)) {
        gfn_Alert("불합격시험의 년도를 선택하십시오.");
        edExamYearSource.SetFocus();
        return false;
    }
    if (gfn_IsNull(cbxCourseSource.Value)) {
        gfn_Alert("불합격시험의 직능을 선택하십시오.");
        cbxCourseSource.SetFocus();
        return false;
    }
    if (gfn_IsNull(edExamOrderSource.Text)) {
        gfn_Alert("불합격시험의 회차를 선택하십시오.");
        edExamOrderSource.SetFocus();
        return false;
    }
    ds_ioExamOrderMgno.ClearData();
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchExamOrderMgno", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioExamOrderMgno=ds_oExamOrderMgno"	// outDs
		,"EOMGTMGNO=" + quote(cbxJibuSource.Value)
         + " EOYEAR=" + quote(edExamYearSource.Text)
         + " EOTCCOURSECD=" + quote(cbxCourseSource.Value)
         + " EOEXAMORDER=" + quote(edExamOrderSource.Text)// args 
		,""
		,false
		,""
		,true
	);
        
    ds_ioTrainingExam.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchExamFailPersonList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioTrainingExam=ds_oTrainingExam"	// outDs
		,"EOMGGTMGNO=" + quote(cbxJibuSource.Value)
         + " EOYEAR=" + quote(edExamYearSource.Text)
         + " EOTCCOURSECD=" + quote(cbxCourseSource.Value)
         + " RETCCOURSECD=" + quote(cbxCourseTarget.Value)
         + " EOEXAMORDER=" + quote(edExamOrderSource.Text)
         + " EOMGNO=" + quote(ds_ioExamOrderMgno.GetColumn(0, "EOMGNO"))// args 
		,"lfn_AfterSearchTrainingExam"
		,false
	);
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
    if (gfn_IsNull(cbxJibuTarget.Value)) {
        gfn_Alert("재접수시험의 지부를 선택하십시오.");
        cbxJibuTarget.SetFocus();
        return false;
    }
    if (gfn_IsNull(edExamYearTarget.Text)) {
        gfn_Alert("재접수시험의 년도를 선택하십시오.");
        edExamYearTarget.SetFocus();
        return false;
    }
    if (gfn_IsNull(cbxCourseTarget.Value)) {
        gfn_Alert("재접수시험의 직능을 선택하십시오.");
        cbxCourseTarget.SetFocus();
        return false;
    }
    if (gfn_IsNull(edExamOrderTarget.Text)) {
        gfn_Alert("재접수시험의 회차를 선택하십시오.");
        edExamOrderTarget.SetFocus();
        return false;
    }
    if ((chkPonyDate.Value == "1" )
            && (left(calSunapDate.Value, 8) <= left(calPonyDate.Value, 8))) {
        gfn_Alert("정산일자는 처리일자보다 이전이어야 합니다.");
        calPonyDate.SetFocus();
        return false;
    }
    
    // 재접수시험 유무 검색
    ds_ioExamOrder.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchExamOrderMgno", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioExamOrder=ds_oExamOrderMgno"	// outDs
		,"EOMGTMGNO=" + quote(cbxJibuTarget.Value)
         + " EOYEAR=" + quote(edExamYearTarget.Text)
         + " EOTCCOURSECD=" + quote(cbxCourseTarget.Value)
         + " EOEXAMORDER=" + quote(edExamOrderTarget.Text)// args 
		,""
		,false
		,""
		,true
	);
	if (ds_ioExamOrder.rowcount = 0) {
        gfn_Alert("재접수 할 시험회차가 존재하지 않습니다.\n다시한번 확인해 주십시오.");
        edExamOrderTarget.SetFocus();
        return false;
	}
	if (ds_ioExamOrder.GetColumn(0, "EOEXAMDATE") < left(GetDate(), 8)) {
        gfn_Alert("시험이 종료된 회차입니다.\n다시한번 확인해 주십시오.");
        edExamOrderTarget.SetFocus();
        return false;
	}
	/*
	for (var i=0;i<ds_ioTrainingExam.rowcount;i++) {
        if (ds_ioTrainingExam.GetColumn(i, "SEL") == 1) {
            
        if (gfn_IsNull(ds_ioTrainingExam.GetColumn(i, "REJUBSUNO"))) {
            cnt += 1;
        }
    }
	*/
	var cnt = 0;
    for (var i=0;i<ds_ioTrainingExam.rowcount;i++) {
        if (ds_ioTrainingExam.GetColumn(i, "SEL") == 1) {
            cnt += 1;
        }
    }
	if (cnt > (ds_ioExamOrder.GetColumn(0, "EOSEATCNT") - ds_ioExamOrder.GetColumn(0, "EOJUBSUCNT"))) {
        gfn_Alert("접수인원을 초과하였습니다.\n접수 가능인원은 " 
                   + (ds_ioExamOrder.GetColumn(0, "EOSEATCNT") - ds_ioExamOrder.GetColumn(0, "EOJUBSUCNT"))
                   + "명입니다.");
        return false;
	}		
    
    if (cnt < 1) {
        gfn_Alert("선택된 불합격자가 없습니다.");
        return false;
    }
    
    // 중복접수 체크
    var ADDQUERY="";
    for ( var i=0; i < ds_ioTrainingExam.GetRowCount(); i++ ) {
        if (ds_ioTrainingExam.GetColumn(i, "SEL") == 1) {
            ADDQUERY += ds_ioTrainingExam.getColumn(i, "EJTPMGNO") + ",";
        }        
    }
    ADDQUERY = left(ADDQUERY, length(ADDQUERY)-1);
    
    ds_ioExamJubsuChk.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchExamJubsuChk", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioExamJubsuChk=ds_oExamJubsuChk"	// outDs
		,"EOMGTMGNO=" + quote(cbxJibuTarget.Value)
         + " EOYEAR=" + quote(edExamYearTarget.Text)
         + " EOTCCOURSECD=" + quote(cbxCourseTarget.Value)
         + " EOEXAMORDER=" + quote(edExamOrderTarget.Text)
         + " ADDQUERY=" + ADDQUERY// args 
		,""
		,false
		,""
		,true
	);
	
	if (ds_ioExamJubsuChk.rowcount > 0) {
        var person = "";
        for (var i=0;i<ds_ioExamJubsuChk.rowcount;i++) {
            person += ds_ioExamJubsuChk.GetColumn(i, "EJPERSONNM") + "   ";
            person += ds_ioExamJubsuChk.GetColumn(i, "BIRTHDAY") + "\n";
        }
        gfn_Alert("중복된 접수자가 있습니다. 다시한번 확인 하십시오.\n\n -- 중복 접수자 -- \n\n"
                    + person);
        return false;
	}
    
    if (!gfn_Confirm("정말로 접수처리 하시겠습니까?")) { // 정말로 수정 저장 하시겠습니까?
        return false;
    }
    
    var ls_Args = "PROCDATE=" + quote(left(calSunapDate.Value, 8)); //  처리일자
    ls_Args += " PONYGUBUN=" + quote(chkPonyDate.Value); //  정산구분
    ls_Args += " PONYDATE=" + quote(left(calPonyDate.Value, 8)); //  정산일자
    ls_Args += " REOMGNO=" + quote(ds_ioExamOrder.GetColumn(0, "EOMGNO")); //  재접수회차 관리번호
    ls_Args += " REOEXAMORDER=" + quote(ds_ioExamOrder.GetColumn(0, "EOEXAMORDER")); //  재접수회차 관리번호
    ls_Args += " REOEXAMDATE=" + quote(ds_ioExamOrder.GetColumn(0, "EOEXAMDATE")); //  재접수회차 관리번호
    
    ds_ioTrainingExamJubsu.ClearData(); // 데이타셋 초기화
    ds_ioTrainingExamSettle.ClearData(); // 데이타셋 초기화
    ds_ioTrainingExamAmt.ClearData(); // 데이타셋 초기화
    ds_ioTrainingExamAmtJubsu.ClearData(); // 데이타셋 초기화
    
    ds_ioTrainingExamJubsu.Copy(ds_ioTrainingExam); // 접수만
    ds_ioTrainingExamSettle.Copy(ds_ioTrainingExam);    // 결제
    ds_ioTrainingExamAmt.Copy(ds_ioTrainingExam);   // 선수납환불 접수 결제 
    ds_ioTrainingExamAmtJubsu.Copy(ds_ioTrainingExam);   // 선수납환불 접수
	
	
    
    for (var i=ds_ioTrainingExam.rowcount;i>=0;i--) {
		
		
		// 접수만 가능 한 데이터
        if (ds_ioTrainingExamJubsu.GetColumn(i, "SEL") == 0
            || ds_ioTrainingExamJubsu.GetColumn(i, "SEL2") == 1
            || ds_ioTrainingExamJubsu.GetColumn(i, "SEL") == "X"
            || ds_ioTrainingExamJubsu.GetColumn(i, "AMTYN") == 1) {
            ds_ioTrainingExamJubsu.DeleteRow(i);
        }
        // 접수 후 결재 만 가능한 데이터
        if (ds_ioTrainingExamSettle.GetColumn(i, "SEL") == 0
            || ds_ioTrainingExamSettle.GetColumn(i, "SEL") == "X"
            || ds_ioTrainingExamSettle.GetColumn(i, "SEL2") == 0
            || ds_ioTrainingExamSettle.GetColumn(i, "SEL2") == "X"
            || ds_ioTrainingExamSettle.GetColumn(i, "AMTYN") == 1) {
            ds_ioTrainingExamSettle.DeleteRow(i);
        }
        // 환불 접수 후 결재만 가능한 데이터
        if (ds_ioTrainingExamAmt.GetColumn(i, "SEL") == 0
            || ds_ioTrainingExamAmt.GetColumn(i, "SEL") == "X"
            || ds_ioTrainingExamAmt.GetColumn(i, "SEL2") == "0"
            || ds_ioTrainingExamAmt.GetColumn(i, "SEL2") == "X"
            || ds_ioTrainingExamAmt.GetColumn(i, "AMTYN") == 0
            || gfn_IsNull(ds_ioTrainingExamAmt.GetColumn(i, "AMTYN"))) {
            ds_ioTrainingExamAmt.DeleteRow(i);
        }
        // 환불 접수만 가능한 데이터
        if (ds_ioTrainingExamAmtJubsu.GetColumn(i, "SEL") == 0
            || ds_ioTrainingExamAmtJubsu.GetColumn(i, "SEL") == "X"
            || ds_ioTrainingExamAmtJubsu.GetColumn(i, "SEL2") == "1"
            || ds_ioTrainingExamAmtJubsu.GetColumn(i, "AMTYN") == 0
            || gfn_IsNull(ds_ioTrainingExamAmtJubsu.GetColumn(i, "AMTYN"))) {
            ds_ioTrainingExamAmtJubsu.DeleteRow(i);
        }		
    } 
    
    tit_clearActionInfo();
    tit_AddMultiActionInfo("training:insertExamReJubsuAmt", "", 0, "");
	tit_AddMultiActionInfo("training:insertExamReJubsuSettle", "", 0, "");	
	tit_AddMultiActionInfo("training:insertExamReJubsu", "", 0, "");
	tit_AddMultiActionInfo("training:insertExamReJubsuAmtJubsu", "", 0, "");
	
    // 서버 호출
    tit_callService(
        ""
        ,""
        ,"ds_iTrainingExamAmt=ds_ioTrainingExamAmt ds_iTrainingExamSettle=ds_ioTrainingExamSettle ds_iTrainingExamJubsu=ds_ioTrainingExamJubsu ds_iTrainingExamAmtJubsu=ds_ioTrainingExamAmtJubsu" // inDs
        //,"ds_iTrainingExamSettle=ds_ioTrainingExamSettle" // inDs
        ,"" // outDs
        ,ls_Args // args
        ,"lfn_AfterSaveTrainingExam"
        ,false
    );    
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 시험자 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchTrainingExam(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    medExamDate.Value = ds_ioExamOrderMgno.GetColumn(0, "EOEXAMDATE");
    medTotalCnt.Value = ds_ioTrainingExam.GetRowCount();
    gdTrainingExam.SetCellProp("head", 13, "text", 0);
    gdTrainingExam.SetCellProp("head", 14, "text", 0);
    //btnPrintRctTotal.Visible = false;
    gfn_SearchMessage(gdTrainingExam); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveTrainingExam(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_Alert("정상적으로 처리되었습니다.");
    gfn_SearchMessage(gdTrainingExam);
    gdTrainingExam.SetCellProp("head", 13, "text", 0);
    gdTrainingExam.SetCellProp("head", 14, "text", 0);
    //btnPrintRctTotal.Visible = true;
    lfn_Search(); // 시험자 조회
}

// 영수증 일괄출력
function btnPrintRctTotal_OnClick(obj)
{    
    // 중복접수 체크
    var ADDQUERY="";
    for ( var i=0; i < ds_ioTrainingExam.GetRowCount(); i++ ) {
        if (!gfn_IsNull(ds_ioTrainingExam.getColumn(i, "REEJMGNO"))) {
            ADDQUERY += ds_ioTrainingExam.getColumn(i, "REEJMGNO") + ",";        
        }        
    }
    ADDQUERY = left(ADDQUERY, length(ADDQUERY)-1);
	
	// 결제정보 가져오기
    ds_ioExamPrint.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchExamReJubsuPrint", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioExamPrint=ds_oExamPrint"	// outDs
		,"EJMGNO=" + ADDQUERY// args 
		,""
		,false 
		,""
		,true
	);
	
	if (ds_ioExamPrint.GetCount() < 1) {
        gfn_Alert("출력할 영수증 정보가 없습니다.");
        return false;
    }
    
    if (!gfn_Confirm("영수증을 일괄출력하시겠습니까?")) {
        return false;
	}
	
    for (i=0;i<ds_ioExamPrint.GetCount();i++) {
        // 영수증 출력 이력을 저장한다.
        gds_oPrintReceiptHistory.ClearData();
    
        tit_clearActionInfo();
        tit_addSingleActionInfo("com:insertPosPrintHistory", "", 0, "");
        // 서버 호출
        tit_callService(
            ""
            ,""
            ,""    // inDs
            ,"gds_oPrintReceiptHistory=ds_oPrintHistory"    // outDs
            ,"POSYEAR=" + quote(ds_ioExamPrint.GetColumn(i, "ESPMYEAR")) // args
                + " POSMGNO=" + quote(ds_ioExamPrint.GetColumn(i, "ESPMMGNO"))
            ,""
            ,false
            ,false
            ,true
        );
    
        if (gds_oPrintReceiptHistory.GetColumn(0, "RESULTCODE") <> "OK") {
            gfn_ErrorMsg(gds_oPrintReceiptHistory.GetColumn(0, "RESULTCODE"), gds_oPrintReceiptHistory.GetColumn(0, "RESULTMSG"));
            return false;
        }           
        
        // 영수증 출력 정보를 조회한다.(POSPRINTM, POSPAYMENT) 
        ds_oPrintReceiptMast.ClearData(); // 데이타셋 초기화
        ds_oPrintReceiptDtl.ClearData(); // 데이타셋 초기화
        tit_clearActionInfo();
        tit_addSearchActionInfo("com:searchPrintReceiptPosMast", false);
        tit_addSearchActionInfo("com:searchPrintReceiptPosDtl", false);
        // 서버 호출
        tit_callService(
            ""
            ,""
            ,""    // inDs
            ,"ds_oPrintReceiptMast=ds_oPrintReceiptMast" // outDs
                + " ds_oPrintReceiptDtl=ds_oPrintReceiptDtl"
            ,"POSYEAR=" + quote(ds_ioExamPrint.GetColumn(i, "ESPMYEAR")) // args
                + " POSMGNO=" + quote(ds_ioExamPrint.GetColumn(i, "ESPMMGNO"))
            ,""
            //,"lfn_AfterPrintReceipt"
            ,false
            ,false
            ,true
        );     
        gfn_PrintReport("frmtraining0020R02ReceiptReIssue.rex", "ds_oPrintReceiptMast||ds_oPrintReceiptDtl", "", false, "영수증");
        //gfn_PrintReport("frmtraining0020R02ReceiptReIssue.rex", "ds_oPrintReceiptMast||ds_oPrintReceiptDtl", "", true);
    }        
}

// 환불신청서 일괄출력
function btnReportBatch_OnClick(obj)
{
    ds_ioRepayReport.ClearData(); // 데이타셋 초기화
    ds_ioRepayReport.Copy(ds_ioTrainingExam);
    
    for (var i=ds_ioTrainingExam.rowcount;i>=0;i--) {
        if (gfn_IsNull(ds_ioRepayReport.GetColumn(i, "TSGUBUNCD"))) {
            ds_ioRepayReport.DeleteRow(i);
        }
    }
    
    if (ds_ioRepayReport.GetCount() < 1) {
        gfn_Alert("출력할 환불 정보가 없습니다.");
        return false;
    }
    
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchExamReJubsuRepayPrint", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioExamPrint=ds_oExamPrint"	// outDs
		,"EOMGGTMGNO=" + quote(cbxJibuSource.Value)
         + " EOYEAR=" + quote(edExamYearSource.Text)
         + " EOTCCOURSECD=" + quote(cbxCourseSource.Value)
         + " EOEXAMORDER=" + quote(edExamOrderSource.Text)// args 
		,""
		,false 
		,""
		,true
	);
	gfn_CallReportPopup("frmtraining1060R01LicenseRepayBatch.rex", "ds_ioExamPrint", "");    
}

