        calPonyDate.Enable = false;
    }
}

// 접수가능회차
function btnSearchExamOrder_OnClick(obj) {
   
    var arrExamOrder = gfn_ExamOrderListPopup("Y", cbxJibuTarget.Value, edExamYearTarget.Text, cbxCourseTarget.Value); // 교육일정관리목록 조회 팝업
    
    if (length(arrExamOrder) > 0) {
		if (cbxCourseSource.Value <> "21") {
			if (arrExamOrder[0] != cbxCourseSource.Value) {
				gfn_Alert("동일직능만 재접수가 가능합니다.");
				btnSearchExamOrder_OnClick(obj);
				return false;
			}
		} else {	// 1급이면
		
			if (cbxJibuSource.Value <> arrExamOrder[4]) {
				gfn_Alert("소속지부 회차만 재접수가 가능합니다.");
				btnSearchExamOrder_OnClick(obj);
				return false;
			
			}
			
			if (!(arrExamOrder[0] == "21" || arrExamOrder[0] == "22")) {
				gfn_Alert("1급 또는 2급소방안전관리자만 재접수가 가능합니다.");
				btnSearchExamOrder_OnClick(obj);
				return false;
			}
		
		}
        cbxJibuTarget.Value = arrExamOrder[4];
        edExamYearTarget.Text = arrExamOrder[1];
        cbxCourseTarget.Value = arrExamOrder[0];
        edExamOrderTarget.Text = arrExamOrder[2];
		lfn_TargetSearch();
    }
}

function gdTrainingExam_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    
	var lobj_Dataset = object(obj.BindDataset);
	
	if (nCell == 1) {
	    if (lobj_Dataset.GetColumn(nRow, "SEL2") == 0) { 
            /*
            if (!gfn_IsNull(lobj_Dataset.GetColumn(nRow, "REJUBSUNO"))) {
                gfn_Alert("이미 재접수가 되어 있습니다.");
                return;
            } else {
                lobj_Dataset.SetColumn(nRow, "SEL", "1");
            }
            */
            lobj_Dataset.SetColumn(nRow, "SEL", "1");
        } 		
	} else if (nCell == 0) {
		if (lobj_Dataset.GetColumn(nRow, "SEL") == 0) { 
			if (lobj_Dataset.GetColumn(nRow, "AMTYN") == 1) {
				 lobj_Dataset.SetColumn(nRow, "SEL2", "1");
			}
		} else if (lobj_Dataset.GetColumn(nRow, "SEL") == 1){	    
            lobj_Dataset.SetColumn(nRow, "SEL2", "0");
        }
	}
}

