	cbxJibuTarget.Value = cbxJibuSource.Value;
}

// 재시험 회차조회
function edExamOrderTarget_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) { // Enter Key
        edExamOrderTarget.Text = iif(gfn_IsNull(edExamOrderTarget.Text), "", lpad(edExamOrderTarget.Text, "0", 3));
        lfn_TargetSearch();
    }	
}



/*
 * 기능 : 재접수 회차 조회
 */
function lfn_TargetSearch(obj) {
    if (gfn_IsNull(cbxJibuTarget.Value)) {
        gfn_Alert("재시험의 지부를 선택하십시오.");
        cbxJibuTarget.SetFocus();
        return false;
    }
    if (gfn_IsNull(edExamYearTarget.Text)) {
        gfn_Alert("재시험의 년도를 선택하십시오.");
        edExamYearTarget.SetFocus();
        return false;
    }
    if (gfn_IsNull(cbxCourseTarget.Value)) {
        gfn_Alert("재시험의 직능을 선택하십시오.");
        cbxCourseTarget.SetFocus();
        return false;
    }
    if (gfn_IsNull(edExamOrderTarget.Text)) {
        gfn_Alert("재시험의 회차를 선택하십시오.");
        edExamOrderTarget.SetFocus();
        return false;
    }
    
	if (edExamOrderSource.Text == edExamOrderTarget.Text) {
		gfn_Alert("불합격시험 회차와 동일한 회차입니다. 다른 회차를 조회하세요.");
		medReExamDate.Value = "";
		edExamOrderTarget.Text = "";
		edExamOrderTarget.SetFocus();
		return false;
	}
    
    
    ds_ioReExamOrderMgno.ClearData();
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchExamOrderMgno", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioReExamOrderMgno=ds_oExamOrderMgno"	// outDs
		,"EOMGTMGNO=" + quote(cbxJibuTarget.Value)
         + " EOYEAR=" + quote(edExamYearTarget.Text)
         + " EOTCCOURSECD=" + quote(cbxCourseTarget.Value)
         + " EOEXAMORDER=" + quote(edExamOrderTarget.Text)// args 
		,""
		,false
		,""
		,true
	);
        
    ds_ioTrainingExam.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchExamFailPersonList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioTrainingExam=ds_oTrainingExam"	// outDs
		,"EOMGGTMGNO=" + quote(cbxJibuSource.Value)
         + " EOYEAR=" + quote(edExamYearSource.Text)
         + " EOTCCOURSECD=" + quote(cbxCourseSource.Value)
         + " RETCCOURSECD=" + quote(cbxCourseTarget.Value)
         + " EOEXAMORDER=" + quote(edExamOrderSource.Text)
         + " EOMGNO=" + quote(ds_ioExamOrderMgno.GetColumn(0, "EOMGNO"))
         + " REEXAMDATE=" + quote(ds_ioReExamOrderMgno.GetColumn(0, "EOEXAMDATE"))// args 
		,"lfn_AfterSearchReExam"
		,false
	);
}

/*
 * 기능 : 시험자 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchReExam(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    if (ds_ioReExamOrderMgno.GetRowCount() == 0) {
    
		gfn_Alert("회차정보가 없습니다. 다시 조회하십시오.");
		medReExamDate.Value = "";
        edExamOrderTarget.SetFocus();
        return false;
    
    }
    
    medReExamDate.Value = ds_ioReExamOrderMgno.GetColumn(0, "EOEXAMDATE");
    medTotalCnt.Value = ds_ioTrainingExam.GetRowCount();
    gdTrainingExam.SetCellProp("head", 13, "text", 0);
    gdTrainingExam.SetCellProp("head", 14, "text", 0);
    gfn_SearchMessage(gdTrainingExam); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}


function btnToExcel_OnClick(obj)
{
    if (ds_ioTrainingExam.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
    }
	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1217";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_ioTrainingExam.GetRowCount();
		parValue += " sTitle=" + quote(gdTrainingExam.UserData);
	ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);	
}

