/*
 * 기능 : 출력
 */
function lfn_Print(obj)
{
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    ds_oExamNumberTypeStat.ClearData(); // 데이타셋 초기화

    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edExamYear.Text)
                + ",회차시작=" + quote(edExamOrderStart.Text)
                + ",회차종료=" + quote(edExamOrderEnd.Text)
                + ",시험시작일=" + quote(left(calDateStart.Value, 8))
                + ",시험종료일=" + quote(left(calDateEnd.Value, 8))
                + ",종별=" + quote(cbxNumberType.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );

    var repeat = "";
    if (cbxCourse.Value == "10" || cbxCourse.Value == "21" || cbxCourse.Value == "22"

        || cbxCourse.Value == "23" || cbxCourse.Value == "50" || cbxCourse.Value == "51" || cbxCourse.Value == "53") {
        repeat = "0";
    } else {
        repeat = cbxExamRepeat.Value;
    }

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchExamTypeScoreStat", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oExamNumberTypeStat=ds_oExamOrderScoreStat"	// outDs
		,"EOMGGTMGNO=" + quote(cbxJibu.Value)
            + " EOTCCOURSECD=" + quote(cbxCourse.Value)
            + " EOYEAR=" + quote(edExamYear.Text)
            + " EOEXAMORDERSTART=" + quote(edExamOrderStart.Text)
            + " EOEXAMORDEREND=" + quote(edExamOrderEnd.Text)
            + " EOEXAMDATESTART=" + quote(left(calDateStart.Value, 8))
            + " EOEXAMDATEEND=" + quote(left(calDateEnd.Value, 8))
            + " REPEAT=" + quote(repeat)
            + " ERNUMTYPE=" + quote(cbxNumberType.Value)// args 
		,"lfn_AfterSearchNumberTypeScoreStat"
		,false
	);      
}


/* 공통 필수 종료 */
function lfn_AfterSearchNumberTypeScoreStat(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    if (ds_oExamNumberTypeStat.GetCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
    // 출력이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edExamYear.Text)
                + ",회차시작=" + quote(edExamOrderStart.Text)
                + ",회차종료=" + quote(edExamOrderEnd.Text)
                + ",시험시작일=" + quote(left(calDateStart.Value, 8))
                + ",시험종료일=" + quote(left(calDateEnd.Value, 8))
                + ",종별=" + quote(cbxNumberType.Text);
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , "종별 성적통계현황출력"
        , ls_HistoryArg
    );
    gfn_CallReportPopup("frmtraining0661R01ExamNumberTypeScoreStatistics.rex"
                      , "ds_oExamNumberTypeStat"
                      , "ORDERSTART=" + edExamOrderStart.Text
                      + "^ORDEREND=" + edExamOrderEnd.Text
					  + "^DATESTART=" + left(calDateStart.Value, 8)
					  + "^DATEEND=" + left(calDateEnd.Value, 8)
                      + "^JIBU=" + cbxJibu.Text
                      + "^GUBUN=" + radSearchGubun.Value
                      + "^TCCOURSECD=" + ds_oExamNumberTypeStat.GetColumn(0, "EOTCCOURSECD")
                      + "^REPEAT=" + cbxExamRepeat.Text);    
}


