/* 사용자 이벤트 함수 */
/* 포커스 아웃될때 자동 0 입력 */
function edExamOrder_OnKillFocus(obj) {
	gfn_ComponentOnKillFocus(obj);

    obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));    
}


function radSearchGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "ORDER") {
        edExamOrderStart.Enable = true;
        edExamOrderEnd.Enable = true;
        edExamOrderStart.Text = "001";
        edExamOrderEnd.Text = "999";
        calDateStart.Value = "";
        calDateEnd.Value = "";
        calDateStart.Enable = false;
        calDateEnd.Enable = false;
        edExamOrderStart.UserData = "S:시작회차";
        edExamOrderEnd.UserData = "S:종료회차";
        calDateStart.UserData = "";
        calDateEnd.UserData = "";
	} else {
        edExamOrderStart.Value = "";
        edExamOrderEnd.Value = "";
        edExamOrderStart.Enable = false;
        edExamOrderEnd.Enable = false;
        calDateStart.Enable = true;
        calDateEnd.Enable = true;
        calDateStart.Value = left(GetDate(), 4) + "0101";
        calDateEnd.Value = GetDate();
        calDateStart.UserData = "S:시작일자";
        calDateEnd.UserData = "S:종료일자";
        edExamOrderStart.UserData = "";
        edExamOrderEnd.UserData = "";
	}
}


function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "10" || strCode == "21" || strCode == "22" || strCode == "23" || strCode == "50" || strCode == "51"|| strCode == "53") {
        lbExamRepeat.Visible = false;
        cbxExamRepeat.Visible = false;
	} else {
        cbxExamRepeat.Visible = true;
        lbExamRepeat.Visible = true;
        cbxExamRepeat.Value = "0";        
	}
}


function btnToExcel_OnClick(obj)
{
    ds_oExamNumberTypeStat.ClearData(); // 데이타셋 초기화

    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edExamYear.Text)
                + ",회차시작=" + quote(edExamOrderStart.Text)
                + ",회차종료=" + quote(edExamOrderEnd.Text)
                + ",시험시작일=" + quote(left(calDateStart.Value, 8))
                + ",시험종료일=" + quote(left(calDateEnd.Value, 8))
                + ",종별=" + quote(cbxNumberType.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );

    var repeat = "";
    if (cbxCourse.Value == "10" || cbxCourse.Value == "21" || cbxCourse.Value == "22"

        || cbxCourse.Value == "23" || cbxCourse.Value == "50" || cbxCourse.Value == "51" || cbxCourse.Value == "53") {
        repeat = "0";
    } else {
        repeat = cbxExamRepeat.Value;
    }

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchExamTypeScoreStat", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oExamNumberTypeStat=ds_oExamOrderScoreStat"	// outDs
		,"EOMGGTMGNO=" + quote(cbxJibu.Value)
            + " EOTCCOURSECD=" + quote(cbxCourse.Value)
            + " EOYEAR=" + quote(edExamYear.Text)
            + " EOEXAMORDERSTART=" + quote(edExamOrderStart.Text)
            + " EOEXAMORDEREND=" + quote(edExamOrderEnd.Text)
            + " EOEXAMDATESTART=" + quote(left(calDateStart.Value, 8))
            + " EOEXAMDATEEND=" + quote(left(calDateEnd.Value, 8))
            + " REPEAT=" + quote(repeat)
            + " ERNUMTYPE=" + quote(cbxNumberType.Value)// args 
		,"lfn_AfterSearchNumberTypeScoreStatExceldown_new"
		,false
	);
}

function lfn_AfterSearchNumberTypeScoreStatExceldown_new(nErrorCode, strErrorMsg)
{
	if(ds_oExamNumberTypeStat.GetRowCount() == 0 )
	{
		gfn_Alert("엑셀 다운로드할 데이터가 없습니다.");
		return;
	}
	else 
	{
		lfn_Excel();
	}
}

