/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdSmsStat, 0); // 멀티 정렬을 실행한다.
}

function lfn_ComponentOnKillFocus(obj)
{
	gfn_ComponentOnKillFocus(obj);
	obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3)); 
}

function lfn_ComponentOnKillFocus2(obj)
{
	gfn_ComponentOnKillFocus(obj);
	obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3)); 
}

function btnPrint_OnClick(obj)
{
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    
    if(!Confirm("발송현황을 출력하시겠습니까?")){  //
        return false;
    }
               ds_ioSmsStat.ClearData(); // 데이타셋 초기화    
		tit_clearActionInfo();
		tit_addCsvSearchActionInfo("training:searchTraining0691SMSNationalStatistics", false);
		// 서버 호출 
		tit_callService(
			""
			,""
			,""	// inDs
			,"ds_ioSmsStat=ds_oSmsStat"	// outDs

			,"SHSENDDATE_B=" + quote(calSendDateStart.value)//  -- 발송일자	
			 +" SHSENDDATE_C=" + quote(calSendDateEnd.value)//  -- 발송일자	
			 
	 		 +" SENDGUBUN=" + quote(radSendGubun.value)//  -- 발송구분
				// args 
			,"lfn_AfterSearchSmsStat"
			,false
			,""
			,true
		);	
	
		
	  if (ds_ioSmsStat.count == "0") {
	    alert("출력할 자료가 없습니다.");
        return false;
	  }

/////////////////////  	 	  
	  gfn_CallReportPopup("frmtraining0691R01SMSNationalStatistics.rex", "ds_ioSmsStat"
                          , "SHSENDDATE_B=" + quote(calSendDateStart.text)//  -- 발송일자
                          + "^SHSENDDATE_C=" + quote(calSendDateEnd.text));//  -- 발송일자	

}

function lfn_localExcel(obj)
{
	if (ds_ioSmsStat.GetRowCount() < 1) {	
		gfn_Alert("출력할 데이터가 존재하지 않습니다.");	
		return false;	
	}	
	lfn_Excel(obj);        

}
