/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }
    // 공통 코드 조회
    //gfn_SearchTrainingCourse("T", ds_oCourse, "Y", "전체"); // 직능 코드 조회
    gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회	
    calSendDateStart.Value = left(GetDate(),8);
    calSendDateEnd.Value = left(GetDate(),4)+"1231";
    
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdSmsStat.Height = nCy - gdSmsStat.Top - 4;
        gdSmsStat.Width = nCx - gdSmsStat.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
var ls_HistoryArg = "";

    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }

    ds_ioReserveSend.ClearData(); // 데이타셋 초기화

///////////////////// 조회이력 남기기
	ls_HistoryArg = "지부=" + quote(cbxJibu.Text)	
				+" 발송일자1=" + quote(calSendDateStart.value)//  -- 발송일자1		
				+" 발송일자2=" + quote(calSendDateEnd.value);//  -- 발송일자2  

    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    ); 
/////////////////////   
	tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchTraining0693SMSReserveSend", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioReserveSend=ds_oReserveSend"	// outDs
		, "GTMGNO=" + quote(cbxJibu.value)   //-- 지부
			+" SENDDATE_A=" + quote(left(calSendDateStart.value,8))//  -- 발송일자	
			+" SENDDATE_B=" + quote(left(calSendDateEnd.value,8))//  -- 발송일자	
						// args 
		,"lfn_AfterSearchSmsStat"
		,false
	);
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchSmsStat(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	medTotalCnt.text = ds_ioReserveSend.count;
    gfn_SearchMessage(gdSmsStat); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.		
}

