}

function lfn_localExcel(obj)
{
    
    if (radWorkGubun.Value == "T") { // 교육접수
        if (ds_ioSmsStat.GetRowCount() < 1) {
            gfn_Alert("출력할 데이터가 존재하지 않습니다.");
            return false;
        }
    } else if (radWorkGubun.Value == "E") { // 시험접수
        	 if (ds_ioSmsStatExam.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
        }
    } else if (radWorkGubun.Value == "S") { // 초빙강사
    
        if (ds_ioSmsStatSpeaker.GetRowCount() < 1) {
            gfn_Alert("출력할 데이터가 존재하지 않습니다.");
            return false;
        }    
    } else if (radWorkGubun.Value == "P") { // 시험감독관

        if (ds_ioSmsStatSupervisor.GetRowCount() < 1) {
            gfn_Alert("출력할 데이터가 존재하지 않습니다.");
            return false;
        }        
    }
    lfn_Excel(obj);  
}
// 상세현황 보기
function gdSmsStat_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    if (gfn_IsNull(ds_ioReserveSend.GetColumn(ds_ioReserveSend.currow, "BSMGNO"))) {
        gfn_Alert("예약발송 상세정보가 없습니다.");
        return false;
    }

    var sArg = "sBsmgno=" + quote(ds_ioReserveSend.GetColumn(ds_ioReserveSend.currow, "BSMGNO"))
    
             + " sBsbccd=" + quote(ds_ioReserveSend.GetColumn(ds_ioReserveSend.currow, "BSBCCD"));
	Dialog("training::frmtraining0694PSMSReserveSendStatistics.xml", sArg, 600, 400, "", -1, -1);

}

function gdSmsStat_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{

	var lobj_Dataset = object(obj.BindDataset);
	if (nCell == 0) { // 선택
        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우
            obj.SetCellProp("head", nCell, "text", 0);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "0");
            }
        } else { // 전체가 이미 선택되지 않은 경우
            obj.SetCellProp("head", nCell, "text", 1);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "1");
            }
        }
	} else { // 헤드 정렬
        lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex);
	}
}


// 예약발송 취소
function btnSendCancel_OnClick(obj)
{

    if(!gfn_CheckChangedGrid(this))  //선택한것이 있는지 체크 메세지 보임
    {
		alert("예약발송 취소대상을 선택해 주십시오.");      
        return false;
    }
	
	if (!gfn_Confirm("선택한 예약발송을 취소 하시겠습니까?")) {
		return false;
	}
	
	
	
	tit_clearActionInfo();

		tit_AddMultiActionInfo(			
			"training:deleteTraining0694ReserveSendCancel"	// insert  	
			, ""	// update  	
			, ""	// delete	
			, ""	// flag column 명	
			, ""	// key sql id	
			, 0	// key 증가 값	
			, "" 	
			, 0 	
			, "N"	// 실행타입	
		);//	}
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iSmsCancel=ds_ioReserveSend:U"// inDs
		,"ds_oStatus=ds_oStatus"	// outDs
		, "" // args
		,"lfn_AfterSendCancel"
		,false
	);	

}



/* 사용자 함수 */
/*
 * 기능 : 문자메시지발송 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
 
function lfn_AfterSendCancel(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    gfn_Alert("예약발송이 취소되었습니다.");
    
    lfn_search();	
    gfn_SearchMessage(gdSmsStat); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

function Form_OnActivate(obj)
{
	gs_SysID = "[stm_01,bsn_3002,dsc_0003]";	// 개인정보접속기록 시스템ID 
}
