#include "LIB::training.js"; // 공통업무 관련

var lb_FileUpload = false;
var ls_FileUploadPath = "/IMGDATA/" + left(getDate(),4) + "/" + SubStr(getDate(),4,2);
var ls_RealFileName = "";
var ls_DirPathName = "";
var lb_FileDelete = false;
var PhotoScanFlag = "";	// 사진스캔 프로그램 (1:SVP-5300프로그램, 2:D400 프로그램)

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회
    gfn_SearchCommonCode(ds_oRegGubun, "PICTUREGUBUN", "Y", "전체"); // 사진등록구분코드 조회
    cbxRegGubun.Value = "";

    if (IsExistVar("sPersonNm") && IsExistVar("sBIRTHDAY")) {
    
        if (length(sPersonNm) >= 1 || length(sBIRTHDAY) >= 1) {
            edPersonNm.Text = sPersonNm;
            medBIRTHDAY.Value = sBIRTHDAY;
            lfn_search(obj); // 강습자 사진 조회
        }
    }
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
        gdImageInfo.Height = nCy - gdImageInfo.Top - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
	if (gfn_isNull(edPersonNm.Text) && gfn_isNull(medBIRTHDAY.Value)) {
	
		gfn_Alert("성명+생년월일을 입력 후 조회하세요!");
		
		return false;
	
	}
	
	imgPicture.ImageID = "";
	imgPicture.Text = "사진";
	
	var param = "";

	if(0 == length(medBIRTHDAY.Value)) {
	
		param = "IIREGGUBUN=" + quote(cbxRegGubun.Value)	
				+ " TPNM=" + quote(edPersonNm.Text)	
				+ " IIAPPROVAL=" + quote(radApproval.Value); // args	
	} /*else if(13 == length(medBIRTHDAY.Value)) {
    
		Https.Sync = true;
		gfn_SearchSCIDI(edPersonNm.Text, medBIRTHDAY.Value); // 실명인증
		Https.Sync = false;
		
		param = "IIREGGUBUN=" + quote(cbxRegGubun.Value)		
					+ " TPNM=" + quote(edPersonNm.Text)		
					+ " TPDI=" + quote(gds_ioSciDI.GetColumn(0, "STRDUP"))		
					+ " IIAPPROVAL=" + quote(radApproval.Value); // args
		
	} */
	else if(length(medBIRTHDAY.Value) < 13) {
	
		param = "IIREGGUBUN=" + quote(cbxRegGubun.Value)	
				+ " TPNM=" + quote(edPersonNm.Text)	
				+ " TPBIRTHDAY=" + quote(medBIRTHDAY.Value)	
				+ " IIAPPROVAL=" + quote(radApproval.Value); // args
	
	}
	
	ds_ioImageInfo.ClearData(); // 데이타셋 초기화	
	tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchImageInfo", false);
	
	tit_callService(	
		""
		,""
		,""	// inDs
		,"ds_ioImageInfo=ds_oImageInfo"	// outDs
		,param // args 
		,"lfn_AfterSearchImageInfo"
		,false
	);
}

/*
 * 기능 : 추가
 */
function lfn_Input(obj) {
	if (!gfn_Confirm(MSGI00070)) { // 신규 추가 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.
        return false;
	}

	mb_IsNewData = true; // 저장버튼이 클릭 설정
    lfn_Cancel(); // 값 초기화
    ls_RealFileName = "";
    ls_DirPathName = "";
    
    // 기본값 설정
    radApproval.Value = "Y";
    radApproval.Enable = false;
    cbxRegGubun.Value = "P";
    cbxRegGubun.Enable = false;
}

/*
 * 기능 : 삭제
 */
function lfn_Delete(obj) {

    var ls_DeleteDirName = ls_DirPathName;
    var ls_DeleteFileName = ls_RealFileName;

    ls_DirPathName = "";
    ls_RealFileName = "";
    
    if (ds_ioImageInfo.currow < 0 || gfn_IsNull(ls_DeleteFileName)) {
        gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
        return false;
    }

    if (!gfn_Confirm(MSGW00030)) { // 정말로 삭제 하시겠습니까?
        return false;
    }

    ds_ioImageInfo.DeleteRow(ds_ioImageInfo.currow); // 선택된 ROW를 삭제한다.

    radApproval.Value = "A";

    tit_clearActionInfo();
	tit_addSaveActionInfo(
		""	// insert
		, ""	// update
		, "training:deleteImageInfo"	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);

	tit_addSaveActionInfo(
		""	// insert
		, ""	// update
		, "training:updateTrainingPerson"	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	
	// 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iImageInfo=ds_ioImageInfo:U ds_iTrainingPerson=ds_ioImageInfo:U"	// inDs
        ,""	// outDs
        ,"" // args 
        ,"lfn_AfterSaveImageInfo"
        ,false
    );

    if (!gfn_isNull(ls_DeleteFileName)) { //첨부 파일이 존재할 경우

        tit_FileDelete( //파일 삭제
            ls_DeleteDirName
            , ls_DeleteFileName
            , false
        );

    }
    
  
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {

    var nRow = 0; //추가인지 수정인지 구분
    var ls_UploadRealName = "";
    var ls_TrainingPerson = "";
    var ls_InsertCheck = "";
    
    cbxRegGubun.Value = "P";

    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }

    if (gfn_isNull(imgPicture.ImageID)) { // 이미지 입력 Validation Check
        gfn_Alert("사진을 등록해 주십시오.");
        return false;
    }

    if (mb_IsNewData) { // 신규 저장
    
        if (!gfn_Confirm(MSGW00020)) { // 정말로 추가 저장 하시겠습니까?
            return false;
        }

        ds_ioImageInfo.InsertRow(0);
        ls_InsertCheck = "training:insertImageInfo";

    } else { // 수정 저장

        if (!gfn_Confirm(MSGW00040)) { // 정말로 수정 저장 하시겠습니까?
            return false;
        }
        
        nRow = ds_ioImageInfo.currow;
        ls_InsertCheck = "training:updateImageInfo";
    }

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("com:searchTrainingPerson", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioTrainingPerson=ds_oTrainingPerson" // outDs
		,"TPBIRTHDAY=" + quote(medBIRTHDAY.Value) // args
		,""
		,false
		,""
		,true
	);

    var ls_Tpmgno = ds_ioTrainingPerson.GetColumn(0,"TPMGNO");

    ds_ioImageInfo.SetColumn(nRow, "TPMGNO", ls_Tpmgno);
    ds_ioImageInfo.SetColumn(nRow, "TPNM", edPersonNm.Text);
    
    ds_ioImageInfo.SetColumn(nRow, "PRNO", medBIRTHDAY.Value);
    ds_ioImageInfo.SetColumn(nRow, "TPBIRTHDAY", medBIRTHDAY.Value);
    ds_ioImageInfo.SetColumn(nRow, "TPAUTHYN", "N");
    ds_ioImageInfo.SetColumn(nRow, "IIREGGUBUN", "P");
    ds_ioImageInfo.SetColumn(nRow, "IIAPPROVAL", iif(radApproval.Value=="Y","Y","N"));
    if(lb_FileUpload) { //파일 업로드일 경우
    
        ls_UploadRealName = medBIRTHDAY.Value + "_" + getDate() + right(timem(),3) + gfn_GetFileExtensionName(fdImageFile.FileName);
        
        fiImageFile = fdImageFile.FilePath + "\\" + fdImageFile.FileName;
        fiImageFile.Open("rb");
        
        ds_ioImageInfo.SetColumn(nRow, "IISAVENM", ls_UploadRealName);
        ds_ioImageInfo.SetColumn(nRow, "IIREALNM", ls_UploadRealName);
        ds_ioImageInfo.SetColumn(nRow, "IIDIRPATH", ls_FileUploadPath);
        ds_ioImageInfo.SetColumn(nRow, "IIIMGBINARY", fiImageFile.ReadBinary());
        ds_ioImageInfo.SetColumn(nRow, "IIIMGSEQ", "0"); //이미지 새로 등록
        
        fiImageFile.Close();
    }

    radApproval.Value = "A";
    
    tit_clearActionInfo();
    tit_addSingleActionInfo(ls_InsertCheck, "", 0, "");
    
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iImageInfo=ds_ioImageInfo:U" // inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterSaveImageInfo"
        ,false
    );
    
    if (lb_FileUpload) { //파일
    
        tit_FileWrite(
            hfImageFile
            , ls_FileUploadPath
            , fdImageFile.FilePath
            , fdImageFile.FileName
            , ls_UploadRealName
            , "U"
            , true
        );
        
        hfImageFile.CloseFile();
    }
    
    //파일지우기
    if (lb_FileDelete) {
        ExecProc("cmd", ' /c "del ' + replace(gs_ScanFileName,'\\\\','\\') + '" ');
        lb_FileDelete = false;
    }

}

