function lfn_Excel(obj) {
    if (ds_ioImageInfo.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return;
    }
}

/**
 * 기능 : 초기화
 */
function lfn_Cancel(obj) {
    gfn_FormInit(this);
	mb_IsNewData = true;

    ds_ioImageInfo.ClearData();
    ls_RealFileName = "";

    lb_FileUpload = false; //업로드 구분을 해지시킨다.
	imgPicture.ImageID = "";
	imgPicture.Text = "사진";
	
    radApproval.Value = "A";
    radApproval.Enable = true;

}

