/* 사용자 함수 */
/*
 * 기능 : 사진 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchImageInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdImageInfo); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveImageInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

//    lfn_Cancel(); // 입력란 초기화
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 사진 조회
}

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {

    btnInput.Enable = bStatus; // 추가 버튼
    btnDelete.Enable = bStatus; // 삭제 버튼
    btnSave.Enable = bStatus; // 수정&저장 버튼
}

/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj) {

	gfn_GridMultiSort(gdImageInfo, 0); // 멀티 정렬을 실행한다.
}

function gdImageInfo_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

    var lobj_Dataset = object(obj.BindDataset);
	ds_ioImageInfo_OnRowPosChanged(lobj_Dataset,nRow,nRow);
}

function ds_ioImageInfo_OnRowPosChanged(obj,nOldRow,nRow) {

    // 그리드의 값을 입력란에 복사한다. 추가상태인 경우 복사 후 저장을 하기 위해 제외한다.
    if (obj.GetRowType(nRow) <> "insert" && nRow <> -1) {
        cbxRegGubun.Value = obj.GetColumn(nRow, "IIREGGUBUN");
        edPersonNm.Text = obj.GetColumn(nRow, "TPNM");
        medBIRTHDAY.Value = obj.GetColumn(nRow, "TPBIRTHDAY");
        radApproval.Value = obj.GetColumn(nRow, "IIAPPROVAL");
        ls_RealFileName = obj.GetColumn(nRow, "IIREALNM");
        ls_DirPathName = obj.GetColumn(nRow, "IIDIRPATH");
        if (gfn_IsNull(radApproval.Value)) {
            radApproval.Value = "N";
        }
        if (length(ls_RealFileName) > 0){
            imgPicture.ImageID = GF_FILE_SERVER_URL + "kems" + ls_DirPathName + "/" + ls_RealFileName;
            imgPicture.Text = "";
        } else {
            imgPicture.ImageID = "";
            imgPicture.Text = "사진";
        }

        lb_FileUpload = false;

	}

    radApproval.Enable = true;
	mb_IsNewData = false; // 저장버튼이 클릭 초기화
}

/**
 * 스캔실행
 */
function btnScanPicture_OnClick(obj) {

	PhotoScanFlag = "1";	// 사진스캔 프로그램 (1:SVP-5300프로그램, 2:D400 프로그램)
	ExecProc(gs_ScanProgramName);

}

function btnPhotoScan_OnClick(obj) {

	PhotoScanFlag = "2";	// 사진스캔 프로그램 (1:SVP-5300프로그램, 2:D400 프로그램)
	ExecProc(gs_PhotoScanProgram);

}

/**
 * 스캔사진불러오기
 */
function btnLoadScanPicture_OnClick(obj) {

	imgPicture.ImageID = "";
	imgPicture.Text = "사진";
	var ls_ScanFileName = "";

	if (PhotoScanFlag == "1") {
		ls_ScanFileName = gs_ScanFileName;	// 기존 실물화상기 파일
	} else if (PhotoScanFlag == "1") {
		ls_ScanFileName = gs_PhotoScanFile;	// 신규 사진스캐너 파일
	} else {
		ls_ScanFileName = gs_PhotoScanFile;
	}	
	var ls_File = split(ls_ScanFileName,"\\");
	
    fdImageFile.FilePath = left(ls_ScanFileName,length(ls_ScanFileName)-length(ls_File[length(ls_file)-1]));
	fdImageFile.FileName = ls_File[length(ls_file)-1];
	
	fiImageFile.FileName = ls_ScanFileName;
	if (fiImageFile.Open()) {
        if (fiImageFile.GetLength() > 300*1024) {
            gfn_Alert("사진 이미지 사이즈가 300KB를 넘겼습니다.");
            fiImageFile.Close();
            return false;
        }
        fiImageFile.Close();
	} else {
        gfn_Alert("스캔 이미지가 없습니다.");
        return;
	}
	
	imgPicture.ImageID = ls_ScanFileName;
	imgPicture.Text = "";
	
	lb_FileUpload = true;
	lb_FileDelete = true;
}

/**
 * 사진찾기
 */
function btnLoadImage_OnClick(obj) {

    fdImageFile.Type = "OPEN";
    
    if (fdImageFile.open()) {
        fiImageFile.FileName = fdImageFile.FilePath + "\\" + fdImageFile.FileName;
        if (fiImageFile.GetLength() > 300*1024) {
            gfn_Alert("사진 이미지 사이즈가 300KB를 넘겼습니다.");
            fiImageFile.Close();
            return false;
        }
        fiImageFile.Close();
        
        imgPicture.ImageID = fdImageFile.FilePath + "\\" + fdImageFile.FileName;
        lb_FileUpload = true;
    }

}

function medBIRTHDAY_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam) {
    if (nChar == 13) {
        lfn_Search();
    }
	gfn_EnterNextFocus(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam);
}
