{	
	tit_clearActionInfo();
	tit_addSingleActionInfo("cust:updateCust6000MatchExceptCancel", "", 0, "");

	if(!gfn_Confirm("'"+ds_ioMFFireObj2.GetColumn(ds_ioMFFireObj2.currow,"OBJ_NM") +"' 연계 제외를 취소하시겠습니까?")) {	
		return;
	}

	// 서버 호출 
	tit_callService( 
		""
		,""
		,""	// inDs
		,"" // outDs 
	    ,"GUBUN='1' BILD_SN="+quote(ds_ioMFFireObj2.GetColumn(ds_ioMFFireObj2.currow,"BILD_SN"))
		,"lfn_AfterMFMatchExceptCancel2"
		,false
		,false
		,true
	);
}

// lfn_MFMatchExceptCancel2 조회후
function lfn_AfterMFMatchExceptCancel2(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
   /* 연계제외처리후  상태값 변경*/     
    // 연계제외대상 상태 값 0(미처리)로 변경 
    ds_ioMFFireObj2.SetColumn(ds_ioMFFireObj2.currow,"MSTATUSCNT","0");

    // 검색구분 (미처리 or 일치완료인 경우 필터링)
    if (tab.page2.radMFSelectGubun.Value == "3") {
		setRow = ds_ioMFFireObj2.currow;
		ds_ioMFFireObj2.Filter("MSTATUSCNT=='-1'"); // 연계제외대상 필터링 
		tab.page2.edMFCount.Text = ds_ioMFFireObj2.GetCount();  // 총 건수 변경
		ds_ioMFFireObj2.row = setRow;
	}	

	// 연계관련 버튼 처리 
	lfn_AllMatchBTN2(ds_ioMFFireObj2.currow);

	// 연계제외버튼변경
	lfn_MFMatchExceptBTN2(ds_ioMFFireObj2.currow);

	// 대상물 선임자정보 상세조회
	lfn_MFObjInfoSearch2(ds_ioMFFireObj2.currow);
}

// 청 그리드 셀 원클릭!!
function tab_page2_gdMFOBJList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	// 안전원 정보 초기 화 
	lfn_MatchColor2("ON",nRow);

	// 연계 제외 여부 버튼 처리 
	lfn_MFMatchExceptBTN2(nRow);

	// 대상물 정보 ,선임자 조회 
	lfn_MFObjInfoSearch2(nRow);
}


//  연계 버튼 처리 
function lfn_MFMatchExceptBTN2(nRow)
{
	var MSTATUS = "";
	if (nRow == "" || nRow == null) {
		MSTATUS = ds_ioMFFireObj2.GetColumn(0,"MSTATUSCNT");
	} else {
		MSTATUS = ds_ioMFFireObj2.GetColumn(nRow,"MSTATUSCNT");
	}	

	if (MSTATUS == 0) {
		tab.page2.btnMFMatchCancel.Visible = false;
		tab.page2.btnMFMatchExcept.Visible = true;
	} else if (MSTATUS == -1) {
		tab.page2.btnMFMatchCancel.Visible = true;
		tab.page2.btnMFMatchExcept.Visible = false;
	} else if (MSTATUS > 0 ) {
		tab.page2.btnMFMatchCancel.Visible = false;
		tab.page2.btnMFMatchExcept.Visible = false;
	} else {
		tab.page2.btnMFMatchCancel.Visible = false;
		tab.page2.btnMFMatchExcept.Visible = false;	
	}
}

