// 청 조회조건변경(연계구분)
function tab_page2_radKFSelectGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_SearchBalance2("12");
}
// 청 조회조건변경 (대상물)
function tab_page2_cbxKFCourseD_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_SearchBalance2("14");
}


function lfn_SearchBalance2(gubun){
	if (gubun == "11") {
		var JIBUCD = tab.page2.cbxKFJibu.Value;
		if (JIBUCD == "1141") {
			 JIBUCD = "1081";
		}
		var row = ds_oFireHead.FindRow("JIBUCD", JIBUCD );	
		tab.page2.cbxMFHead.Value = ds_oFireHead.GetColumn(row,"CD");
		lfn_FilterFireStation(tab.page2.cbxMFHead.Value);
	} else if (gubun == "12") {
		 tab.page2.radMFSelectGubun.Value = tab.page2.radKFSelectGubun.Value;
	} else if (gubun == "14") {
		if (tab.page2.cbxKFCourseD.Index == 3) {
			tab.page2.cbxMFObjGubun.Value = "P02090100";
			//tab.page2.cbxMFPBLINSTT.Value = "Y";
		} else if (tab.page2.cbxKFCourseD.Index == 1) {
			tab.page2.cbxMFObjGubun.Value = "P02090100";
			//tab.page2.cbxMFPBLINSTT.Value = "N";
		} else if (tab.page2.cbxKFCourseD.Index == 4) {
			tab.page2.cbxMFObjGubun.Value = "P02090200";
			//tab.page2.cbxMFPBLINSTT.Value = "Y";			
		} else if (tab.page2.cbxKFCourseD.Index == 2) {
			tab.page2.cbxMFObjGubun.Value = "P02090200";
			//tab.page2.cbxMFPBLINSTT.Value = "N";
		} else {
			tab.page2.cbxMFObjGubun.Value = "";
			//tab.page2.cbxMFPBLINSTT.Value = "N";
		}
	}
}


