}



function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam)
{
	switch (nChar) {
		case 123 : // 화면출력(F12)
			if (obj.btnPrintScreen.Enable) {
				obj.lfn_PrintScreen();
			}
			break;
		case 27 : // 종료(Esc)
			if (obj.btnEnd.Enable) {
				Close();
			}
			break;
	}
}
