Window.Script:
/*
 * 고객정보명 : frmCOM1000PTrainingPersonList.xml
 * 설명 : 고객정보검색팝업
 * 작성일 : 2009.07.23
 * 작성자 : 김동현
 */

#include "LIB::COMMON.js"; // 공통 관련

var ACCNTJIBU = ""; // 지부코드
var sharchYN = "Y"; // 검색 조건 변경 여부

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sKeyword")) {
        edKeyword.Text = sKeyword;
        if (gfn_IsNull(sKeyword)) {
        } else {
            lfn_search(obj); // 고객정보 조회
        }
    }
    
    rdPaymentCondition.SetFocus();
    lfn_LoadData();
    
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

/*
 * 기능 : 초기 데이터 로드
 */
function lfn_LoadData() {
	// 초기 적용년도
    medYear.Text = substr(getDate(),0,4);
    lbRange.Text = "순번 범위(발급순번으로 조회가능)";
    
    // 버튼배치
    edsRange1.Visible = true;
    edsRange2.Visible = true;
    edsRange1.MoveWindow(190,355,116,20);
    edsRange2.MoveWindow(323,355,116,20);
    
    var serviceName = "otherincome:selectAccntjibu";
     
    ds_ioOtherCode.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo(serviceName, false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oJIBU=ds_oJIBU"	// outDs
		,"PDEPTCD=" + quote(gds_oUserInfo.GetColumn(0, "PDEPTCD"))// args
		,"lfn_AfterselectAccntjibu"
		,false
		,""
		,true
	);
	
}

function lfn_AfterselectAccntjibu(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    if (ds_oJIBU.GetCount() > 0) {
		ACCNTJIBU = ds_oJIBU.GetColumn(0, "ACCNTJIBU");
    }
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
		case 13 :  // 조회(return)
            lfn_Search();
            
            if("0" == rdPaymentCondition.Value) {
				edsRange1.SetFocus();
            } else if("1" == rdPaymentCondition.Value) {
				medsRange1.SetFocus();
            } else if("2" == rdPaymentCondition.Value) {
				medsRange1.SetFocus();
            } else if("3" == rdPaymentCondition.Value) {
				medsRange1.SetFocus();
            }
            break;
        case 27 :  // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

	// 지급조서 조회
	var serviceName = "otherincome:searchOtherIncome";
    
    ds_iSearchotherincome.ClearData();
    ds_oSearchotherincome.ClearData();

    if ("0" == rdPaymentCondition.Value) { // 순번

        if("" == medYear.Value) {
			alert("적용년도를 입력해 주세요.");
			return false;
        } else if("" == edsRange1.Text || "" == edsRange2.Text) {
			alert("순번 범위를 입력해 주세요.");
			return false;
        }
        
        ds_iSearchotherincome.InsertRow(0);	
		ds_iSearchotherincome.SetColumn(0, "I_PROC", "1");           // 지급일자
		ds_iSearchotherincome.SetColumn(0, "I_JIBU", ACCNTJIBU);     // 지부
		ds_iSearchotherincome.SetColumn(0, "I_YEAR", medYear.Value); // 관리년도
		ds_iSearchotherincome.SetColumn(0, "I_FR", edsRange1.Text);  // 조회조건 (시작)
		ds_iSearchotherincome.SetColumn(0, "I_TO", edsRange2.Text);  // 조회조건 (종료)
		
    } else if ("1" == rdPaymentCondition.Value) { // 지급일자

        if("" == medsRange1.Value) {
			alert("지급일자를 입력해 주세요.");
			return false;
        }
        
        ds_iSearchotherincome.InsertRow(0);	
		ds_iSearchotherincome.SetColumn(0, "I_PROC", "0");            // 지급일자
		ds_iSearchotherincome.SetColumn(0, "I_JIBU", ACCNTJIBU);      // 지부
		ds_iSearchotherincome.SetColumn(0, "I_FR", medsRange1.Value); // 조회조건 (시작)
        
    } else if ("2" == rdPaymentCondition.Value) { // 지급일자 [범위]

        if("" == medsRange1.Value || "" == medsRange2.Value) {
			alert("지급일자 범위를 입력해 주세요.");
			return false;
        }
        
        ds_iSearchotherincome.InsertRow(0);	
		ds_iSearchotherincome.SetColumn(0, "I_PROC", rdPaymentCondition.Value); // 지급일자
		ds_iSearchotherincome.SetColumn(0, "I_JIBU", ACCNTJIBU);                // 지부
		ds_iSearchotherincome.SetColumn(0, "I_FR", medsRange1.Value);           // 조회조건 (시작)
		ds_iSearchotherincome.SetColumn(0, "I_TO", medsRange2.Value);           // 조회조건 (종료)
        
    } else if ("3" == rdPaymentCondition.Value) { // 입력일자

        if("" == medsRange1.Value || "" == medsRange2.Value) {
			alert("입력일자 범위를 입력해 주세요.");
			return false;
        }
        
        ds_iSearchotherincome.InsertRow(0);	
		ds_iSearchotherincome.SetColumn(0, "I_PROC", rdPaymentCondition.Value); // 지급일자
		ds_iSearchotherincome.SetColumn(0, "I_JIBU", ACCNTJIBU);                // 지부
		ds_iSearchotherincome.SetColumn(0, "I_FR", medsRange1.Value);           // 조회조건 (시작)
		ds_iSearchotherincome.SetColumn(0, "I_TO", medsRange2.Value);           // 조회조건 (종료)
    }
    
    tit_clearActionInfo();
	tit_addSearchActionInfo(serviceName, false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iSearchotherincome=ds_iSearchotherincome" // inDs
		,"ds_oSearchotherincome=ds_oSearchotherincome" // outDs
		,"" // args
		,"lfn_AftersearchOtherincome"
		,false
		,""
		,true
	);
	trace(ds_iSearchotherincome.SaveXML());
}

function lfn_AftersearchOtherincome(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    trace(ds_oSearchotherincome.SaveXML());
    if (ds_oSearchotherincome.GetCount() > 0) {
		trace(ds_oSearchotherincome.SaveXML());
    }
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchOtherPerson(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    if (ds_oOtherKeyword.GetCount() > 0) {
        gdOtherPerson.SetFocus();
    } else {
        edKeyword.SetFocus();
    }
    sharchYN = "Y"; // 조회 후 선택 가능하도록 플래그 변경함
    btnSelect.Enable = true;  // 조회 후 선택 가능하게 함   
}

/* 고객 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

function btnSelect_OnClick(obj) {
    if (ds_oSearchotherincome.GetCount() < 1) {
        Close();
    }

    var arrArg = Array(2);
    arrArg[0] = ds_oSearchotherincome.GetColumn(ds_oSearchotherincome.currow, "OPMGNO");
    arrArg[1] = ds_oSearchotherincome.GetColumn(ds_oSearchotherincome.currow, "OIYEAR");

    Close(arrArg);
}

function gdOtherPerson_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    if(sharchYN == "N") {
        alert("조회를 다시 하시고 선택 바랍니다.");
    } else {
        btnSelect_OnClick(obj);
    }
}

function edKeyword_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
    if (nChar == 13) { // Enter Key
        lfn_Search();
    } else {
        btnSelect.Enable = false;
        sharchYN = "N"; // 조회 조건 변경시 선택하여 진행 하지 못하도록 함(반드시 재조회 필요)
    }
}

function gdOtherPerson_OnEnterDown(obj,nRow,nCell,strVal,nPivotIndex)
{
	btnSelect_OnClick();
}

/*
 * 기능 : 조회구분 선택
 */
function rdPaymentCondition_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(0 == strCode) { // 순번
		// 적용년도 Visible
		lbOiyear.Visible = true;
		sOiyear.Visible = true;
		medYear.Visible = true;
		
		// 초기 적용년도
		lbRange.Text = "순번 범위(발급순번으로 조회가능)";
		
		// 버튼배치
		edsRange1.Visible = true;
		edsRange2.Visible = true;
		medsRange1.Visible = false;
		medsRange2.Visible = false;
		lbs.Visible = true;
		
		edsRange1.MoveWindow(190,355,116,20);
		edsRange2.MoveWindow(323,355,116,20);
	} else if(1 == strCode) { // 지급일자
		// 적용년도 Invisible
		lbOiyear.Visible = false;
		sOiyear.Visible = false;
		medYear.Visible = false;
		
		// 초기 적용년도
		lbRange.Text = "지급일자 (지급일자 순으로 조회가능)";
		
		// 버튼배치
		edsRange1.Visible = false;
		edsRange2.Visible = false;
		medsRange1.Visible = true;
		medsRange2.Visible = false;
		lbs.Visible = false;
		
		medsRange1.MoveWindow(190,355,116,20);
		medsRange2.MoveWindow(323,355,116,20);
	} else if(2 == strCode) { // 지급일자[범위]
		// 적용년도 Invisible
		lbOiyear.Visible = false;
		sOiyear.Visible = false;
		medYear.Visible = false;
		
		// 초기 적용년도
		lbRange.Text = "지급일자 범위(지급일자 순으로 조회가능)";
		
		// 버튼배치
		edsRange1.Visible = false;
		edsRange2.Visible = false;
		medsRange1.Visible = true;
		medsRange2.Visible = true;
		lbs.Visible = true;
		
		medsRange1.MoveWindow(190,355,116,20);
		medsRange2.MoveWindow(323,355,116,20);
	} else if(3 == strCode) { // 입력일자
		// 적용년도 Invisible
		lbOiyear.Visible = false;
		sOiyear.Visible = false;
		medYear.Visible = false;
		
		// 초기 적용년도
		lbRange.Text = "입력일자 범위(입력일자 순으로 조회가능)";
		
		// 버튼배치
		edsRange1.Visible = false;
		edsRange2.Visible = false;
		medsRange1.Visible = true;
		medsRange2.Visible = true;
		lbs.Visible = true;
		
		medsRange1.MoveWindow(190,355,116,20);
		medsRange2.MoveWindow(323,355,116,20);
	}
	// 조회값 초기화
	edsRange1.Text = "";
	edsRange2.Text = "";
	medsRange1.Value = "";
	medsRange2.Value = "";
}

function gdIncomePayment_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(sharchYN == "N") {
        alert("조회를 다시 하시고 선택 바랍니다.");
    } else {
        btnSelect_OnClick(obj);
    }
}
