var vsCurrentPage = 1;
var vsCountPerPage = 10;
var vnTotalCount =0;
var sPostAddr1 = "";
var sPostAddr2 = "";

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
	//콤보박스에 시도 데이터를 셋팅
	gfn_Form_OnLoadCompleted(obj);
	ds_oJuso_imsi.ClearData();
	trace(sPostAddr1);
	trace(sPostAddr2);

	vsKeyword.Text = sPostAddr1 + " " + sPostAddr2;
	lfn_Search();
}

/* 선택된 값을 부모창으로 전달하는 함수 */
function btnSelect_OnClick(obj)
{
	var arrArg = Array(29);
    arrArg[0] = ds_oJuso.GetColumn(ds_oJuso.currow,"ROADADDR");       	//전체도로명 주소
    arrArg[1] = ds_oJuso.GetColumn(ds_oJuso.currow,"ROADADDRPART1");  	//도로명주소(참고항목제외)
    arrArg[2] = ds_oJuso.GetColumn(ds_oJuso.currow,"ROADADDRPART2");  	//도로명주소 참고항목
    arrArg[3] = ds_oJuso.GetColumn(ds_oJuso.currow,"JIBUNADDR");     	//지번정보
    arrArg[4] = ds_oJuso.GetColumn(ds_oJuso.currow,"ENGADDR");        	//도로명주소(영문)
    arrArg[5] = ds_oJuso.GetColumn(ds_oJuso.currow,"ZIPNO");         	//우편번호
    arrArg[6] = ds_oJuso.GetColumn(ds_oJuso.currow,"ADMCD");          	//행정구역코드
    arrArg[7] = ds_oJuso.GetColumn(ds_oJuso.currow,"RNMGTSN"); 	    	//도로명코드
    arrArg[8] = ds_oJuso.GetColumn(ds_oJuso.currow,"BDMGTSN");  	    //건물관리번호
    arrArg[9] = ds_oJuso.GetColumn(ds_oJuso.currow,"BDKDCD");        	//공통주택여부(1:공동주택, 0:비공동주택)
    arrArg[10] = ds_oJuso.GetColumn(ds_oJuso.currow,"SINM");          	//시도명
    arrArg[11] = ds_oJuso.GetColumn(ds_oJuso.currow,"SGGNM");	        //시군구명
    arrArg[12] = ds_oJuso.GetColumn(ds_oJuso.currow,"EMDNM");         	//읍면동명
    arrArg[13] = ds_oJuso.GetColumn(ds_oJuso.currow,"LINM");         	//법정리명
    arrArg[14] = ds_oJuso.GetColumn(ds_oJuso.currow,"UDRTYN");        	//지하여부(0:지상, 1:지하)
    arrArg[15] = ds_oJuso.GetColumn(ds_oJuso.currow,"MTYN");      		//산여부 (0:대지, 1:산)
    arrArg[16] = ds_oJuso.GetColumn(ds_oJuso.currow,"EMDNO");      		//읍면동일련번호
	arrArg[17] = left(ds_oJuso.GetColumn(ds_oJuso.currow,"ADMCD"),2);   	// 시도
	arrArg[18] = substr(ds_oJuso.GetColumn(ds_oJuso.currow,"ADMCD"),3,3);   // 시군구
	arrArg[19] = substr(ds_oJuso.GetColumn(ds_oJuso.currow,"ADMCD"),6,3);  	// 동
	arrArg[20] = substr(ds_oJuso.GetColumn(ds_oJuso.currow,"ADMCD"),10,2); 	// 리
	if(ds_oJuso.GetColumn(ds_oJuso.currow,"MTYN") == 0)						// 산
    {
		arrArg[21] = "N";
    }
    else 
    {
		arrArg[21] = "Y";
    }
	arrArg[22] = ds_oJuso.GetColumn(ds_oJuso.currow,"LNBRMNNM");		// 	본 번지
	arrArg[23] = ds_oJuso.GetColumn(ds_oJuso.currow,"LNBRSLNO");		// 	부 번지
	arrArg[24] = ds_oJuso.GetColumn(ds_oJuso.currow,"RN");				// 	도로 명
	arrArg[25] = ds_oJuso.GetColumn(ds_oJuso.currow,"BULDMNNM");		// 	본 건물 번호
	arrArg[26] = ds_oJuso.GetColumn(ds_oJuso.currow,"BULDSLNO");		// 	부 건물 번호
	arrArg[27] = ds_oJuso.GetColumn(ds_oJuso.currow,"BDNM");			//건물 명
    arrArg[28] = ds_oJuso.GetColumn(ds_oJuso.currow,"DETBDNMLIST");     //건물 명 기타

    Close(arrArg);
}

/* 도로명 주소를 조회하는 함수 */
function lfn_Search(obj)
{
	//=======================================================================================================================================================//
	// 아래소스는 navigate2에 데이터를 가져온걸 미리 볼 수 있도록 한다. ( 개발자만 ) 
    ds_oJuso.ClearData();

	var tsResultType = "xml/html";
	var tsConfmKey = "U01TX0FVVEgyMDIyMTAzMTEwNDIyMjExMzE2MTg=";  // 승인key
	var tsKeyword =  urlEncodeUtf8_New(vsKeyword.text);	
	var pageUrl = "";
	pageUrl = "https://business.juso.go.kr/addrlink/addrLinkApi.do?currentPage=1&countPerPage=200&keyword="+tsKeyword+"&confmKey=U01TX0FVVEgyMDIyMTAzMTEwNDIyMjExMzE2MTg=";

	AxMSIE0.Navigate2(pageUrl);
	//=======================================================================================================================================================//
	// 이건 mxl로가져와서 xmlhttp, xmldom으로 파싱하여 ds에 setcolumn한다.
	var XML = CreateObject("Microsoft.XMLHTTP");
	XML.open("GET","https://business.juso.go.kr/addrlink/addrLinkApi.do?currentPage=1&countPerPage=200&keyword="+tsKeyword+"&confmKey=U01TX0FVVEgyMDIyMTAzMTEwNDIyMjExMzE2MTg=",false); //서비스 페이지를 호출합니다.
	XML.Send();

	var xslfile = XML.ReSPONSEText;	
	var XMLDoc = CreateObject("Microsoft.XMLDOM");	
	XMLDoc.async = false;	
	XMLDoc.loadxml(xslfile);

	var root = XMLDoc.documentElement;
	var juso = root.getElementsByTagName("juso");
	for(var i=0; i<juso.length; i++)
	{
		var node = juso[i];
		var childNodes = node.childNodes;

		grdRoadAddr.Visible = false;
		ds_oJuso.AddRow(i);

		ds_oJuso.SetColumn(ds_oJuso.currow, "ROADADDR",childNodes[0].text);			//전체도로명 주소
		ds_oJuso.SetColumn(ds_oJuso.currow, "ROADADDRPART1",childNodes[1].text);	//도로명주소(참고항목제외)
		ds_oJuso.SetColumn(ds_oJuso.currow, "ROADADDRPART2",childNodes[2].text);	//도로명주소 참고항목
		ds_oJuso.SetColumn(ds_oJuso.currow, "JIBUNADDR",childNodes[3].text);		//지번정보
		ds_oJuso.SetColumn(ds_oJuso.currow, "ENGADDR",childNodes[4].text);			//도로명주소(영문)
		ds_oJuso.SetColumn(ds_oJuso.currow, "ZIPNO",childNodes[5].text);			//우편번호
		ds_oJuso.SetColumn(ds_oJuso.currow, "ADMCD",childNodes[6].text);			//행정구역코드
		ds_oJuso.SetColumn(ds_oJuso.currow, "RNMGTSN",childNodes[7].text);			//도로명코드
		ds_oJuso.SetColumn(ds_oJuso.currow, "BDMGTSN",childNodes[8].text);			//건물관리번호
		ds_oJuso.SetColumn(ds_oJuso.currow, "DETBDNMLIST",childNodes[9].text);		//상세건물명
		ds_oJuso.SetColumn(ds_oJuso.currow, "BDNM",childNodes[10].text);			//건물명
		ds_oJuso.SetColumn(ds_oJuso.currow, "BDKDCD",childNodes[11].text);			//공통주택여부(1:공동주택, 0:비공동주택)
		ds_oJuso.SetColumn(ds_oJuso.currow, "SINM",childNodes[12].text);			//시도명
		ds_oJuso.SetColumn(ds_oJuso.currow, "SGGNM",childNodes[13].text);			//시군구명
		ds_oJuso.SetColumn(ds_oJuso.currow, "EMDNM",childNodes[14].text);			//읍면동명
		ds_oJuso.SetColumn(ds_oJuso.currow, "LINM",childNodes[15].text);			//법정리명
		ds_oJuso.SetColumn(ds_oJuso.currow, "RN",childNodes[16].text);				//도로명
		ds_oJuso.SetColumn(ds_oJuso.currow, "UDRTYN",childNodes[17].text);			//지하여부(0:지상, 1:지하)
		ds_oJuso.SetColumn(ds_oJuso.currow, "BULDMNNM",childNodes[18].text);		//건물본번
		ds_oJuso.SetColumn(ds_oJuso.currow, "BULDSLNO",childNodes[19].text);		//건물부본(부본이 없는 경우 0)	
		ds_oJuso.SetColumn(ds_oJuso.currow, "MTYN",childNodes[20].text);			//건물부본(부본이 없는 경우 0)
		ds_oJuso.SetColumn(ds_oJuso.currow, "LNBRMNNM",childNodes[21].text);		//건물부본(부본이 없는 경우 0)
		ds_oJuso.SetColumn(ds_oJuso.currow, "LNBRSLNO",childNodes[22].text);		//건물부본(부본이 없는 경우 0)
		ds_oJuso.SetColumn(ds_oJuso.currow, "EMDNO",childNodes[23].text);			//건물부본(부본이 없는 경우 0)

		edZipNo.Text = childNodes[5].text;
		edRoadAddrPart1.Text = childNodes[1].text;

		// 원래 여기서 읍면동 데이터를 뒤에다가 붙이는데 도로명주소에 읍면동이 안붙어있는 경우에는 
		// 읍면동면을 별도로 가져와서 붙여준다.
		if(childNodes[2].text == NULL || childNodes[2].text == "")
		{
			edRoadAddrPart2.Text = childNodes[14].text;
		}
		else {
			edRoadAddrPart2.Text = childNodes[2].text;
		}	

		grdRoadAddr.Visible = true;
	}

	destroyObject(XMLDoc); 		
}  	

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 115 : // 저장(F4)
			btnSave_OnClick(obj);
            break;
        case 121 : // 초기화(F10)
			lfn_Cancel(obj);
            break;
        case 123 : // 화면출력(F12)
			PrintScreen(true);
            break;
    }
}


/* 공통 필수 종료 */
function lfn_End(obj)
{
	close();
}

/* VScroll이 최종 Row까지 이동되었을 때 발생하는 Event 
 페이징처리 
 */
function grdRoadAddr_OnScrollLast(obj){

	if(vnTotalCount < vsCurrentPage * vsCountPerPage){
		return;
	}
	vsCurrentPage = vsCurrentPage + 1;

	lfn_Search(this);

}

/* Grid의 Vscroll이 마지막까지 Scroll 될 때마다 발생되는 Event */
function grdRoadAddr_OnTrackLast(obj)
{
	grdRoadAddr_OnScrollLast(this);
}

function vsKeyword_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == "13")
	{
		lfn_Search();
	}
}

function grdRoadAddr_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	btnSelect_OnClick();
}

function btnSave_OnClick(obj)
{
	var arrArg = Array(29);
    arrArg[0] = ds_oJuso.GetColumn(ds_oJuso.currow,"ROADADDR");       	//전체도로명 주소
    // 직접입력일 경우에는 아래 수기입력된 주소를 가져온다.
    if(chkRoadInput.Value == 0)
    {
		arrArg[1] = ds_oJuso.GetColumn(ds_oJuso.currow,"ROADADDRPART1");  	//도로명주소(참고항목제외)
		arrArg[2] = ds_oJuso.GetColumn(ds_oJuso.currow,"ROADADDRPART2");  	//도로명주소 참고항목
    }
    else if(chkRoadInput.Value == 1)
    {
		arrArg[1] = edRoadAddrPart1.Text;  	//도로명주소(참고항목제외)
		arrArg[2] = edRoadAddrPart2.Text;  	//도로명주소 참고항목
    }
    arrArg[3] = ds_oJuso.GetColumn(ds_oJuso.currow,"JIBUNADDR");     	//지번정보
    arrArg[4] = ds_oJuso.GetColumn(ds_oJuso.currow,"ENGADDR");        	//도로명주소(영문)

    if(chkRoadInput.Value == 0)
    {
		arrArg[5] = ds_oJuso.GetColumn(ds_oJuso.currow,"ZIPNO");         	//우편번호
	}
	else
	{
		arrArg[5] = edZipNo.Value;
	}
    arrArg[6] = ds_oJuso.GetColumn(ds_oJuso.currow,"ADMCD");          	//행정구역코드
    arrArg[7] = ds_oJuso.GetColumn(ds_oJuso.currow,"RNMGTSN"); 	    	//도로명코드
    arrArg[8] = ds_oJuso.GetColumn(ds_oJuso.currow,"BDMGTSN");  	    //건물관리번
    arrArg[9] = ds_oJuso.GetColumn(ds_oJuso.currow,"BDKDCD");        	//공통주택여부(1:공동주택, 0:비공동주택)
    arrArg[10] = ds_oJuso.GetColumn(ds_oJuso.currow,"SINM");          	//시도명
    arrArg[11] = ds_oJuso.GetColumn(ds_oJuso.currow,"SGGNM");	        //시군구명
    arrArg[12] = ds_oJuso.GetColumn(ds_oJuso.currow,"EMDNM");         	//읍면동명
    arrArg[13] = ds_oJuso.GetColumn(ds_oJuso.currow,"LINM");         	//법정리명
    arrArg[14] = ds_oJuso.GetColumn(ds_oJuso.currow,"UDRTYN");        	//지하여부(0:지상, 1:지하)
    arrArg[15] = ds_oJuso.GetColumn(ds_oJuso.currow,"MTYN");      		//산여부 (0:대지, 1:산)
    arrArg[16] = ds_oJuso.GetColumn(ds_oJuso.currow,"EMDNO");      		//읍면동일련번호
	arrArg[17] = left(ds_oJuso.GetColumn(ds_oJuso.currow,"ADMCD"),2);   	// 시도
	arrArg[18] = substr(ds_oJuso.GetColumn(ds_oJuso.currow,"ADMCD"),3,3);   // 시군구
	arrArg[19] = substr(ds_oJuso.GetColumn(ds_oJuso.currow,"ADMCD"),6,3);  	// 동
	arrArg[20] = substr(ds_oJuso.GetColumn(ds_oJuso.currow,"ADMCD"),10,2); 	// 리
	if(ds_oJuso.GetColumn(ds_oJuso.currow,"MTYN") == 0)						// 산
    {
		arrArg[21] = "N";
    }
    else 
    {
		arrArg[21] = "Y";
    }
	arrArg[22] = ds_oJuso.GetColumn(ds_oJuso.currow,"LNBRMNNM");		// 	본 번지
	arrArg[23] = ds_oJuso.GetColumn(ds_oJuso.currow,"LNBRSLNO");		// 	부 번지
	arrArg[24] = ds_oJuso.GetColumn(ds_oJuso.currow,"RN");				// 	도로 명
	arrArg[25] = ds_oJuso.GetColumn(ds_oJuso.currow,"BULDMNNM");		// 	본 건물 번호
	arrArg[26] = ds_oJuso.GetColumn(ds_oJuso.currow,"BULDSLNO");		// 	부 건물 번호
	arrArg[27] = ds_oJuso.GetColumn(ds_oJuso.currow,"BDNM");			//건물 명
    arrArg[28] = ds_oJuso.GetColumn(ds_oJuso.currow,"DETBDNMLIST");     //건물 명 기타


	//arrArg[29] = edRoadDetailAddr.Text + ds_oJuso.GetColumn(ds_oJuso.currow,"ROADADDRPART2");  	//도로명주소 참고항목


	arrArg[29] = edRoadDetailAddr.Text + edRoadAddrPart2.Text;  	//도로명주소 참고항목;  	//도로명주소 참고항목


    Close(arrArg);	
}

function chkRoadInput_OnClick(obj,strValue)
{
	if(strValue == 1)
	{
		ds_oJuso.ClearData();
		edZipNo.Enable = true;
		edZipNo.Readonly = false;
		edRoadAddrPart1.Enable = true;
		edRoadAddrPart2.Enable = true;
	}
	else {

		edZipNo.Enable = false;
		edZipNo.Readonly = true;
		edRoadAddrPart1.Enable = false;
		edRoadAddrPart2.Enable = false;
	}
}

function ds_oJuso_OnRowPosChanged(obj,nOldRow,nRow)
{
	edZipNo.Text = ds_oJuso.GetColumn(nRow, "ZIPNO");
	edRoadAddrPart1.Text = ds_oJuso.GetColumn(nRow, "ROADADDRPART1");

	// 원래 여기서 읍면동 데이터를 뒤에다가 붙이는데 도로명주소에 읍면동이 안붙어있는 경우에는 
	// 읍면동면을 별도로 가져와서 붙여준다.
	var roadaddr2 = ds_oJuso.GetColumn(nRow, "ROADADDRPART2");

	if(roadaddr2 == NULL || roadaddr2 == "")
	{
		edRoadAddrPart2.Text = ds_oJuso.GetColumn(nRow, "EMDNM");	
	}
	else {
		edRoadAddrPart2.Text = ds_oJuso.GetColumn(nRow, "ROADADDRPART2");	
	}
}
