#include "LIB::training.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.
    // 공통 코드 조회
    lfn_Search(); // 폼코드 조회
    
}

function Form_OnKeyDown(obj,objJubsuerObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do
    gfn_Form_OnKeyDown(obj,objJubsuerObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
        gdBizform.Height = nCy - gdBizform.Top - 4;
        gdBizform.Width = nCx - gdBizform.Left - 4;
    }
}
 function lfn_Search(obj) {
	
	// 폼코드 조회
	var serviceName = "com:searchFcbizcode";
	
	ds_ioBizformcode.ClearData(); // 데이타셋 초기화
	
	tit_clearActionInfo();
	tit_addSearchActionInfo(serviceName, false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioBizformcode=ds_ioBizformcode" // outDs
		,"TYPE='1'" // args
		,"lfn_AfterFcbizcode"
		,false
		,""
		,true
	);
	
 }
 
function lfn_AfterFcbizcode(nErrorCode, strErrorMsg) {
    
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
}

// 바로가기
function gdBizform_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell == 2) {
		//alert(ds_ioBizformcode.GetColumn(nRow, "FCLINK"));
		if(ds_ioBizformcode.GetRowCount() == 0) {	
			gfn_Alert("바로가기 연결할 자료가 없습니다.");	
			return;	
		} else {	
			var arrSystem = split(ds_ioBizformcode.GetColumn(nRow, "FCLINK"),":");
			var ArrMenuInfo = "";
			
			ArrMenuInfo = gfn_SearchMenuInfo(arrSystem[0], ds_ioBizformcode.GetColumn(nRow, "FCLINK"));	//메뉴정보조회
	
			if(length(ArrMenuInfo) > 0 ) {	
				ls_SystemGubun = arrMenuInfo[7];						//"cust";	
				gs_SystemMenuId = arrMenuInfo[5];						//"메뉴아이디	
				global.mnuLeft.tabMenu.tabpgMenu.Text = arrMenuInfo[6];	//"고객관리";	
				var ls_MenuId = arrMenuInfo[0];							//"0000000262";	
				var ls_MenuTitle = arrMenuInfo[1];						//"실무교육접수관리";	
				var ls_MenuUrl = arrMenuInfo[2];						//"cust::frmcust1025MEduJubsuManagement.xml";	
					
				global.mnuTop.tabTopMenu.TabIndex = gds_oTopMenu.FindRow("MSYSGUBUN", ls_SystemGubun);	
				gfn_OpenLinkChildWindow(ls_MenuUrl, ls_MenuTitle, ls_MenuId, ""); 
	
			} else {	
				gfn_Alert("해당 메뉴를 찾을 수 없습니다.");	
			}	
		}
	
	} 
	
}

