#include "LIB::cust.js"; // 공통업무 관련

var ls_sPNM = ""; // 이름 
var ls_sPIHIDNUM = ""; // 주민번호(생년월일)

// 11.27 선반영
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

	rdGubun.Value = "Y";// 자격정보 (연계(Y), 직적입력 (N))
	rdGubun_OnChanged();

    if (IsExistVar("sPNM")) {
        ls_sPNM = sPNM; 
    }
    if (IsExistVar("sPIHIDNUM")) {
        ls_sPIHIDNUM = sPIHIDNUM; 
    }
    /*
	trace("ls_sPNM : " + ls_sPNM);
	trace("ls_sPIHIDNUM : " + ls_sPIHIDNUM);
	trace("ls_sPersonkey : " + ls_sPersonkey);
	*/
	edsPNM.Text = ls_sPNM;
	edsPIHIDNUM.Text = sPIHIDNUM;

    lfn_Search();
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oPersonkeyInfoList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust3030PPersonkeyInfoList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oPersonkeyInfoList=ds_oPersonkeyInfoList"	// outDs
			,"PNM=" + quote(ls_sPNM)
				+ " PIHIDNUM=" + quote(ls_sPIHIDNUM) // args 
		,"lfn_AfterSearch"
		,false
	);	
}

/* 공통 필수 종료 */

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_SearchMessage(gdList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 이벤트 함수 종료 */


/* 사용자 함수 */
function gdList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    btnSelect_OnClick(obj);
}

function btnSelect_OnClick(obj) {

	var arrArg = Array(8);
	arrArg[0] = rdGubun.Value; // 조회결과선택 -결과선택(Y), 해당없음 (N)

	if(rdGubun.Value == 'N'){
		arrArg[1] = "";		
	}else{

		if(ds_oPersonkeyInfoList.RowCount() < 0 || ds_oPersonkeyInfoList.currow < 0 ){
			gfn_Alert("선택 목록이 없습니다.");
			return false;
		}

		arrArg[1] = ds_oPersonkeyInfoList.GetColumn(ds_oPersonkeyInfoList.currow,  "PERSONKEY"); //개인식별번호
	}
    Close(arrArg);    
}

function gdList_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if (nChar == 13) { // Enter Key
        btnSelect_OnClick(obj);
    }
}


function rdGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(rdGubun.Value == 'N'){
		// 선택한 정보 해제 하고 disable 
		for(i = 0; i < ds_oPersonkeyInfoList.RowCount(); i++) {
			if(ds_oPersonkeyInfoList.GetColumn(i, "SEL") == '1') {
				ds_oPersonkeyInfoList.SetColumn(i, "SEL",'0');
			}
		}
		gdList.Enable = false;	
	}else{	
		gdList.Enable = true;
	}
}

function gdList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

}
