}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
        gdBoard.Height = nCy - gdBoard.Top - 4;
    }
}

/**
 * 엑셀 출력
 */
function lfn_Excel(obj) {
    if (ds_oBoard.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

    ds_oBoard.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cti:searchBoardenv", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oBoard=ds_oBoard"	// outDs
		,"BBOARDGUBUN=" + quote(ls_BoardGubun)
            + " BTITLE=" + quote(edTitle.Text)	// args 
		,""
		,false
	);

}
/* 공통 필수 종료 */

/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdBoard, 0); // 멀티 정렬을 실행한다.
}

function edTitle_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if (nChar == 13) {
        lfn_search();
	}
}

function gdBoard_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

	if(nCell == 3) {
        lfn_DownloadFile(obj);
	} else {
        var sArg = "sBoardSeq=" + quote(ds_oBoard.GetColumn(nRow, "BSEQ"))
            + " sFormTitle=" + quote(sFormTitle);
        return Dialog("cti::frmcti0024PBoardContents.xml", sArg, 600, 400, "", -1, -1);
    }
}

function lfn_DownloadFile(obj){
    fdAttachFile.Type = "SAVE";
    fdAttachFile.FileName = ds_oBoard.GetColumn(ds_oBoard.currow, "AFSAVENM");

    if (fdAttachFile.open()) {
        var arrRtn = tit_fileRead(
            hfAttachFile
            , ds_oBoard.GetColumn(ds_oBoard.currow, "AFDIRPATH")
            , ds_oBoard.GetColumn(ds_oBoard.currow, "AFREALNM")
            , fdAttachFile.FilePath
            , fdAttachFile.FileName
        );

        gfn_Alert(MSGI00060); //다운로드 완료 메시지
    }
}


function gdBoard_OnMouseOver(obj,nPosX,nPosY,nRow,nCell,nPivotIndex) {
	ds_oBoard.row = nRow;
}
