Window.Script:
/*
 * 프로그램명 : frmcust0017PAdditionHistory.xml
 * 설명 : 부가정보이력
 * 작성일 : 2009.12.06
 * 작성자 : 이규희
 */

#include "LIB::cust.js"; // 공통업무 관련

var CMGNO = "";
var sFlag = false;
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 공통 코드 조회
    gfn_SearchCommonCode(ds_oAdditionInfo, "ADCD", "Y", "전체");
    cbxAdditionGubun.Value = "";

    if (IsExistVar("sCmgno")) {
        if (gfn_IsNull(sCmgno)) {
            gfn_Alert("고객정보가 없습니다.");
            Close();
        }
        CMGNO = sCmgno;
        lfn_Search();
    }
}

function Form_OnUnloadCompleted(obj) {
    lfn_SearchLastSeq();
    close(ds_oSeq.GetColumn(0, "SEQ"));
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    lfn_SearchLastSeq();
    close(ds_oSeq.GetColumn(0, "SEQ"));
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oAdditionList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("cust:searchCust0017AdditionList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oAdditionList=ds_oAdditionList"	// outDs
		,"ADCMGNO=" + quote(CMGNO)
		,"lfn_AfterSearchAddition"
		,false
	);
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {    
    if (!gfn_Confirm("변경된 정보로 수정하시겠습니까?")) {
        return false;
	}

    ds_oAdditionList.SetColumn(ds_oAdditionList.currow, "ADCD", cbxAdditionGubun.Value);
    ds_oAdditionList.SetColumn(ds_oAdditionList.currow, "ADCONTENT", edKeyWord.Text);
    ds_oAdditionList.SetColumn(ds_oAdditionList.currow, "ADDELGUBUN", cbxStatus.Value);

    tit_clearActionInfo();
    tit_addSaveActionInfo(
        ""	// insert
        , "cust:updateCust0017AdditionHistoryModify"	// update
        , ""	// delete
        , ""	// flag column 명
        , ""	// key sql id
        , 0	// key 증가 값
        , "" 
        , 0 
        , "N"	// 실행타입
    );
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iAdditionList=ds_oAdditionList:U"	// inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterSaveAgentInfo"
        ,false
    );
}
/* 공통 필수 종료 */

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchAddition(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    //trace(ds_oAdditionList.SaveXML());

    gfn_SearchMessage(gdAddtionList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

function lfn_AfterSaveAgentInfo(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    sFlag = true;

    lfn_Search(); // 교육일정관리 조회
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
}

/* 이벤트 함수 종료 */
function ds_oAdditionList_OnRowPosChanged(obj,nOldRow,nRow)
{
    if (ds_oAdditionList.GetColumn(nRow, "ADREGDATE") <> left(GetDate(),8)) {
        cbxAdditionGubun.Enable = false;
        edKeyWord.Enable = false;
        cbxStatus.Enable = false;
        btnSave.Enable = false;
        //btnDelete.Enable = false;
    } else {
        cbxAdditionGubun.Enable = true;
        edKeyWord.Enable = true;
        cbxStatus.Enable = true;
        btnSave.Enable = true;
        //btnDelete.Enable = true;
    }
	cbxAdditionGubun.Value = ds_oAdditionList.GetColumn(nRow, "ADCD");
	edKeyWord.Text = ds_oAdditionList.GetColumn(nRow, "ADCONTENT");
	cbxStatus.Value = ds_oAdditionList.GetColumn(nRow, "ADDELGUBUN1");
}

function lfn_SearchLastSeq() {
    ds_oSeq.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("cust:searchCust0017AdditionHistoryLastSeq", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oSeq=ds_oSeq"	// outDs
		,"ADCMGNO=" + quote(CMGNO)
		,""
		,false
		,""
		,true
	);
}
