/*
 * 기능 : 출력
 */
function lfn_Print(obj){
    lfn_Search();
    course = "";
    if (ds_oCustomerList.GetCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }

    if (radReport.Value=="1") {

        // 고객대장: 값 1
        gfn_CallReportPopup("frmcust0100R01CustomerList.rex"
                       , "ds_oCustomerList"
                       , "rd_TITLE=" + quote(radOrder.Text)
         );
    }
    else {
        // 겸직자대장: 값 0
        gfn_CallReportPopup("frmcust0100R02CustomerList.rex"
                       , "ds_oCustomerList"
                       , "rd_TITLE=" + quote(radOrder.Text)
         );
    }
}


function lfn_Search(obj) {
    //용도
    if (radMgno.Value=="1") {
        if(gfn_IsNull(medMgno1.Value)) {
            gfn_Alert("고객번호를" + MSGW00010);		//고객번호을(를) 입력해 주십시오.
            medMgno1.SetFocus();
            return;
        }
    }
    //선임일자 선택 시
    if (radDate.Value=="1") {
        if (gfn_IsNull(calStartDate.Value) || gfn_IsNull(calEndDate.Value)) {
            gfn_Alert("등록일자를 입력하십시오.");
            calStartDate.SetFocus();            
            return false;
        }
        if (calStartDate.Value > calEndDate.Value) {
            gfn_Alert("시작일자가 종료일자보다 늦습니다.");
            calStartDate.Value = left(GetDate(), 8);   //calDateStart.Value = left(GetDate(),4)+"0101"
            return false;
        }
    }
     if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }

    if(cbxCourse1.Value != "")
        course += "'" + cbxCourse1.Value + "'" + ",";
    if(cbxCourse2.Value != "")
        course += "'" + cbxCourse2.Value + "'" + ","; 
    if(cbxCourse3.Value != "")
        course += "'" + cbxCourse3.Value + "'" + ","; 
    if(cbxCourse4.Value != "")
        course += "'" + cbxCourse4.Value + "'" + ","; 
    if(cbxCourse5.Value != "")
        course += "'" + cbxCourse5.Value + "'" + ","; 
    if(cbxCourse6.Value != "")
        course += "'" + cbxCourse6.Value +"'" + ","; 
    course = left(course,length(course)-1); 

    //조회
    lfn_SearchMember();
}


/*
 * 기능 : 조회
 */
function lfn_SearchMember(obj)
{   
    // 조회이력을 저장한다.  
    ls_HistoryArg = "조건=" + quote(radJogun.Value);
                + ",자료유형=" + quote(radDataType.Value)
                + ",고객구분=" + quote(radMemberGubun.Value)
                + ",용도=" + quote(cbxBusinessType1.Value)
                + ",지부=" + quote(cbxJibu.Text)
                + ",지역=" + quote(cbxRegion1.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",선임S=" + quote(left(calStartDate.value,8))
                + ",선임E=" + quote(left(calEndDate.value,8))
                + ",정렬순서=" + quote(radOrder.value)
                + ",관리제외구분=" + quote(radExceptGubun.Value)
                + ",업무대행=" + quote(radAgencyGubun.Value)
				+ ",등록년월S=" + quote(left(calStartRegYYMM.value,6))
                + ",등록년월E=" + quote(left(calEndRegYYMM.value,6));
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
    var courseD="";
    if(cbxCourseD1.Value != "")
        courseD += "'" + cbxCourseD1.Value + "'" + ",";
    if(cbxCourseD2.Value != "")
        courseD += "'" + cbxCourseD2.Value + "'" + ","; 
    if(cbxCourseD3.Value != "")
        courseD += "'" + cbxCourseD3.Value + "'" + ","; 
    if(cbxCourseD4.Value != "")
        courseD += "'" + cbxCourseD4.Value + "'" + ","; 
    if(cbxCourseD5.Value != "")
        courseD += "'" + cbxCourseD5.Value + "'" + ","; 
    if(cbxCourseD6.Value != "")
        courseD += "'" + cbxCourseD6.Value +"'" + ","; 
    courseD = left(courseD,length(courseD)-1); 

    ds_oCustomerList.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();

    // S : 2010.03.26 김민호 : 관리제외 대상일 경우 사유와 제외일자까지 SELECT //

    if(radExceptGubun.Value != '1') {		
		tit_addCsvSearchActionInfo("cust:searchCust0100CustomerList", false);
	} else {
		tit_addCsvSearchActionInfo("cust:searchCust0100NotAdminCustomerList", false); // 관리제외 대상일 경우
	}	
	// E : 2010.03.26 김민호 : 관리제외 대상일 경우 사유와 제외일자까지 SELECT //

//	tit_addSearchActionInfo("cust:searchCust0100CustomerList", false);
	// 서버 호출 
	tit_callService(
         ""
		,""
		,""	// inDs
		,"ds_oCustomerList=ds_oCustomer"	// outDs
		,"REPORTGUBUN=" + quote(radReport.Value)      //레포트구분====
            + " JOGUN=" + quote(radJogun.Value)//조건[주민번호정상여부]
            + " RADDATATYPE=" + quote(radDataType.Value)//자료유형 삭제유무 확인
            + " GUBUN=" + quote(radGijun.Value)    // 지부 (1), 소방본부(2) 구분
            + " JIBU=" + quote(cbxJibu.Value)       //지부
			+ " BONBU=" + quote(substr(cbxBonbu.Value,0,2))    // 소방본부
	    	+ " RCREGGUBUN=" + quote(radRegion.Value)//지역구분=====
            + " RCREGCD1=" +quote(cbxRegion1.value)    //지역 소방서 1
            + " RCREGCD2=" +quote(cbxRegion2.value)    //지역 소방서 2 
            + " MEMBERGUBUN=" + quote(radMemberGubun.Value)//회원구분
            + " SELECT_COURSE=" +quote(radCourse.value) //직능선택구분====
            + " COURSE_GUBUN=" +quote(cbxCourse.value)  //대표직능선택시 (1개 선택시)
            + " COURSE_GUBUN_IN=" +quote(course)        //여러개선택시
            + " SELECT_COURSE_D=" +quote(radCourseD.value) //직능선택구분====
            + " COURSE_GUBUN_D=" +quote(cbxCourseD.value)  //세부직능선택시 (1개 선택시)
            + " COURSE_GUBUN_IN_D=" +quote(courseD)        //여러개선택시
            + " SELECT_CPERSON=" +quote(radCpersonGubun.value) //인력구분선택
            + " CPERSONGUBUN=" +quote(cbxCpersonGubun1.value)  //인력구분            
            + " RADBUSINESS_GUBUN=" + quote(radBusinessType.Value)//용도구분====
            + " BUSINESSTYPE1=" +quote(cbxBusinessType1.Value)//용도1
            + " BUSINESSTYPE2=" +quote(cbxBusinessType2.Value)//용도1            
            + " RADMGNO_GUBUN=" + quote(radMgno.Value)//고객번호구분====
            + " MEMBERNO1=" +quote(medMgno1.Value)//고객번호1
            + " MEMBERNO2=" +quote(medMgno2.Value)//고객번호2
            + " STARTDATE=" +quote(calStartDate.value)  //선임일자1
            + " ENDDATE=" +quote(calEndDate.value)      //선임일자2
            + " RADORDER=" +quote(radOrder.value)       //정렬순
            + " MINAPFEE=" +quote(radMinapfee.Value)	// 미납금액 구분     
            + " EXCEPTGUBUN=" +quote(radExceptGubun.Value)	//관리구분
            + " AGENCYGUBUN=" +quote(radAgencyGubun.Value)	//업무대행여부
            + " STARTREGYYMM=" +quote(left(calStartRegYYMM.value,6))  //등록년월S
            + " ENDREGYYMM=" +quote(left(calEndRegYYMM.value,6))      //등록년월E

		, "lfn_AfterSearchMemberReport"
		,false
		,""
		,true		
    );     
}

function lfn_AfterSearchMemberReport(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);     
        course = "";
        return;
    }    
}


//초기화
function lfn_Cancel(obj)
{
    lfn_CancelL(obj);
}

function lfn_CancelL(obj)
{
    radOrder.Value = "1";       
    radReport.Value = "1";
	radCourse_OnChanged();
    radRegion_OnChanged();
    radCourseD_OnChanged();
    radBusinessType_OnChanged();
    medMgno1.value ="";
    medMgno2.value ="";    
    medMgno1.Enable = false;
    medMgno2.Enable = false;
    radReport.SetFocus();
    calStartDate.Enable =  false;
    calEndDate.Enable =  false;
    calStartRegYYMM.Enable =  false;
    calEndRegYYMM.Enable =  false;    
}


/*
*콤보박스 변경 이벤트 시작
*/
//지부 변경시
function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{ 
//    ds_oRegion.ClearData();
	gfn_SearchCodeRegion(strCode, ds_oRegion, "0");	//지역 코드 조회
    cbxRegion1.Index = 0;
    cbxRegion2.index = ds_oRegion.rowcount-1;
}

// 소방본부 변경시
function cbxBonbu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_FilterFireStation(strCode);
	cbxRegion1.Index = 0;
	cbxRegion2.Index = ds_oRegion.rowcount-1;
}

/*==  소방서 조회 (소방본부 변경시)  ==*/
function lfn_FilterFireStation(sFilterGubun) {	
	ds_oFireCd.UnFilter();		
    ds_oRegion.Clear();
    ds_oRegion.copy(ds_oFireCd);    

    //  필터링
	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
        ls_FilterExpr += "CTCD='" + trim(sFilterGubun) + "'";
        ds_oRegion.Filter(ls_FilterExpr);
    }
}

//지역--라디오 버튼 선택시
function radRegion_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if (strCode == "1") { // 선택 
        cbxRegion1.Enable =  true;
        cbxRegion2.Enable =  true;                       
        cbxRegion1.SetFocus();
        cbxRegion1.Index = 0;
        cbxRegion2.index = ds_oRegion.rowcount-1;    
	} else {              // 전체
        cbxRegion1.Enable =  false;
        cbxRegion2.Enable =  false;                
        cbxRegion1.Index = 0;
        cbxRegion2.index = ds_oRegion.rowcount-1;    
	}
}

//대표직능
function radCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 0) {          //선택
        cbxCourse.SetFocus();
        cbxCourse.Enable = true;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;        
        cbxCourse.index = 0;
        cbxCourse_OnChanged(object("cbxCourse"),"");
        cbxCourse1.index = 0;
        cbxCourse2.index = 0;
        cbxCourse3.index = 0;
        cbxCourse4.index = 0;
        cbxCourse5.index = 0;
        cbxCourse6.index = 0;
        cbxCourseD.Enable = false;	
        cbxCourseD.index = 0;        
	} else if(strCode == 1){     //다중선택
        cbxCourse1.SetFocus();
        cbxCourse.Enable = false;
        cbxCourse1.Enable = true;
        cbxCourse2.Enable = true;
        cbxCourse3.Enable = true;
        cbxCourse4.Enable = true;
        cbxCourse5.Enable = true;
        cbxCourse6.Enable = true;        
        cbxCourse.index = 0;
        cbxCourse_OnChanged(object("cbxCourse"),"");
        cbxCourse1.index = 0;
        cbxCourse2.index = 0;
        cbxCourse3.index = 0;
        cbxCourse4.index = 0;
        cbxCourse5.index = 0;
        cbxCourse6.index = 0;
        radCourseD.Enable = false;
        radCourseD.Index="0";	
        cbxCourseD.Enable = false;
        cbxCourseD.index = 0;        
	} else {                    //전체
        cbxCourse.Enable = false;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;
        cbxCourse.index = 0;
        cbxCourse_OnChanged(object("cbxCourse"),"");
        cbxCourse1.index = 0;
        cbxCourse2.index = 0;
        cbxCourse3.index = 0;
        cbxCourse4.index = 0;
        cbxCourse5.index = 0;
        cbxCourse6.index = 0;       
        radCourseD.Enable = false;
        radCourseD.Index="0";
        cbxCourseD.Enable = false;
        cbxCourseD.index = 0;        

	}
}

//대표직능 --> 선택콤보
function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(gfn_IsNull(strCode)){
        radCourseD.Enable = false;
        radCourseD.Index="0";
        cbxCourseD.Enable = false;
        cbxCourseD1.Enable = false;
        cbxCourseD2.Enable = false;
        cbxCourseD3.Enable = false;
        cbxCourseD4.Enable = false;
        cbxCourseD5.Enable = false;
        cbxCourseD6.Enable = false;   
        radCpersonGubun.Index = 0;
        radCpersonGubun.Enable = false;
        lfn_FilterPersonGubun(strCode);        
    }else{
        gfn_SearchCustomerDetailCourse(cbxCourse.Value,ds_ioCourseD, "0", "선택");// 세부고객직능 조회	
        radCourseD.Enable = true;
        radCourseD.Index="0";
        cbxCourseD.Enable = false;
        cbxCourseD1.Enable = false;
        cbxCourseD2.Enable = false;
        cbxCourseD3.Enable = false;
        cbxCourseD4.Enable = false;
        cbxCourseD5.Enable = false;
        cbxCourseD6.Enable = false; 
        cbxCourseD.index = 0;
        cbxCourseD1.index = 0;
        cbxCourseD2.index = 0;
        cbxCourseD3.index = 0;
        cbxCourseD4.index = 0;
        cbxCourseD5.index = 0;
        cbxCourseD6.index = 0;

        radCpersonGubun.Index = 1;
        radCpersonGubun.Enable = true;
        lfn_FilterPersonGubun(strCode);
    }
    radCpersonGubun_OnChanged(object("radCpersonGubun"),radCpersonGubun.Value);

}

//세부직능-->선택콤보
function cbxCourseD_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(gfn_IsNull(strCode)){
        radCpersonGubun.Enable = false;
        radCpersonGubun.Index = 0;
        lfn_FilterPersonGubun(strCode);

    }else{
        radCpersonGubun.Index = 1;
        radCpersonGubun.Enable = true;
        lfn_FilterPersonGubun(strCode);
    }
    radCpersonGubun_OnChanged(object("radCpersonGubun"),radCpersonGubun.Value);
}


//세부직능
function radCourseD_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 0) {          //선택
//        gfn_SearchCustomerDetailCourse(cbxCourse.Value,ds_ioCourseD, "0", "선택");// 세부고객직능 조회	
//        cbxCourseD.SetFocus();
        cbxCourseD.Enable = true;
        cbxCourseD1.Enable = false;
        cbxCourseD2.Enable = false;
        cbxCourseD3.Enable = false;
        cbxCourseD4.Enable = false;
        cbxCourseD5.Enable = false;
        cbxCourseD6.Enable = false; 
        cbxCourseD.index = 0;
        cbxCourseD1.index = 0;
        cbxCourseD2.index = 0;
        cbxCourseD3.index = 0;
        cbxCourseD4.index = 0;
        cbxCourseD5.index = 0;
        cbxCourseD6.index = 0;

	} else if(strCode == 1){     //다중선택
        radCpersonGubun.Enable=false;
        radCpersonGubun.Index=0;
        cbxCpersonGubun1.Value = "";
        cbxCpersonGubun1.Enable=false;
        cbxCourseD.Enable = false;
        cbxCourseD1.Enable = true;
        cbxCourseD2.Enable = true;
        cbxCourseD3.Enable = true;
        cbxCourseD4.Enable = true;
        cbxCourseD5.Enable = true;
        cbxCourseD6.Enable = true;        
        cbxCourseD.index = 0;
        cbxCourseD1.index = 0;
        cbxCourseD2.index = 0;
        cbxCourseD3.index = 0;
        cbxCourseD4.index = 0;
        cbxCourseD5.index = 0;
        cbxCourseD6.index = 0;

	} else {                    //전체
        if(cbxCourse.Value=="30"||cbxCourse.Value=="50") {
            radCpersonGubun.Enable=true;
            radCpersonGubun.Index=1;
            cbxCpersonGubun1.Index=0;
            cbxCpersonGubun1.Enable=true;          

            lfn_FilterPersonGubun(cbxCourse.Value);

        }
        else{
            radCpersonGubun.Enable=false;
            radCpersonGubun.Index=0;
            cbxCpersonGubun1.Value = "";
            cbxCpersonGubun1.Enable=false;
        }
        cbxCourseD.Enable = false;
        cbxCourseD1.Enable = false;
        cbxCourseD2.Enable = false;
        cbxCourseD3.Enable = false;
        cbxCourseD4.Enable = false;
        cbxCourseD5.Enable = false;
        cbxCourseD6.Enable = false;
        cbxCourseD.index = 0;
        cbxCourseD1.index = 0;
        cbxCourseD2.index = 0;
        cbxCourseD3.index = 0;
        cbxCourseD4.index = 0;
        cbxCourseD5.index = 0;
        cbxCourseD6.index = 0;

	}
}

/*
 * 인력구분 필터링
 */
function lfn_FilterPersonGubun(sFilterGubun) {

	if(gfn_IsNull(sFilterGubun)) {
        sFilterGubun = 99;
    }

	ds_oPersonGubun.UnFilter();
	var fGubun;		//필터구분(직능)
	switch(sFilterGubun)
	{
		//공공기관
		case 13 :
			fGubun = "10";
		break;
		case 14 :
			fGubun = "10";
		break;
		//위험물
		case 30 :
			fGubun = "30";
		break;
		//소방기술자
		case 50 :
			fGubun = "50";
		break;
		//개인회원
		case 60 :
			fGubun = "60";
		break;
        default :
            fGubun = "99";
        break;
	}

	var ls_FilterExpr = "";
    if (!gfn_IsNull(fGubun)) {
        ls_FilterExpr += "PCD=='" + trim(fGubun) + "'";
    }
    ds_oPersonGubun.Filter(ls_FilterExpr);

    if(fGubun == "99") {
        radCpersonGubun.Index = 0;
        radCpersonGubun.Enable = false;
        radCpersonGubun_OnChanged(object("radCpersonGubun"),radCpersonGubun.Value);
    }
}
