function btnToExcel_OnClick(obj)
{	
    lfn_Search();
	if(ds_oCustomerList.rowcount == 0){
		gfn_Alert("데이터가 없습니다.");
		return;
	}

	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1406";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_oCustomerList.GetRowCount();

		if(radExceptGubun.Value != '1' && radMinapfee.Value == 0) {
			parValue += " sTitle=" + quote(gdList1.UserData);			// 관리대상 미제외 , 미납출력 안함
		} else if (radExceptGubun.Value != '1' && radMinapfee.Value == 1) { 
			parValue += " sTitle=" + quote(gdList2.UserData);			// 관리대상 미제외 , 미납출력 함
		} else if (radExceptGubun.Value == '1' && radMinapfee.Value == 0) { 
			parValue += " sTitle=" + quote(gdList3.UserData);			// 관리대상 제외 , 미납출력 안함
		} else if (radExceptGubun.Value == '1' && radMinapfee.Value == 1) { 
			parValue += " sTitle=" + quote(gdList4.UserData);			// 관리대상 제외 , 미납출력 함
		}
	var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}

	var strText = radReport.Text;

	// S : 2010.03.26 김민호 : 관리대상 제외일 경우 사유와 제외일자까지 SELECT 
	//     2010.04.13 김민호  :  미납금액 출력 여부 
	if(radExceptGubun.Value != '1' && radMinapfee.Value == 0) {
		setExcelGridHeader(gdList1,strText);			// 관리대상 미제외 , 미납출력 안함
	} else if (radExceptGubun.Value != '1' && radMinapfee.Value == 1) { 
		setExcelGridHeader(gdList2,strText);			// 관리대상 미제외 , 미납출력 함
	} else if (radExceptGubun.Value == '1' && radMinapfee.Value == 0) { 
		setExcelGridHeader(gdList3,strText);			// 관리대상 제외 , 미납출력 안함
	} else if (radExceptGubun.Value == '1' && radMinapfee.Value == 1) { 
		setExcelGridHeader(gdList4,strText);			// 관리대상 제외 , 미납출력 함
	}
	// E : 2010.03.26 김민호 : 관리대상 제외일 경우 사유와 제외일자까지 SELECT 
	//     2010.04.13 김민호  :  미납금액 출력 여부 
}

//엑셀 함수 입니다. (위에 제목을 넣기 위함)
function setExcelGridHeader(grdObj,strText){
	create("grid","grdExcel",'BindDataSet="' + grdObj.BindDataset +'"');
	grdExcel.contents = grdObj.Contents;	
	var headCnt = getHeadCount(grdExcel);
	var totCol  = grdExcel.GetColCount();
	var irow;
	for(var i = 0 ; i < headCnt ; i++ )	{
		irow = toInteger(grdExcel.GetCellProp("head",i,"row"));
		grdExcel.SetCellProp("head",i,"row",irow+1);
	}
	var str_temp = grdExcel.GetCurContents();
	var str_Head = '<cell align="left" col="0" colspan="' + totCol + '" display="text" text="' + strText + '"/>';	
	str_temp = replace(str_temp, '</head>',str_Head +'</head>');
	grdExcel.contents = str_temp;
	grdExcel.ExportExcelEx("sheet1","A1",false,true,true);
	Destroy(grdExcel);
}
//엑셀 함수 입니다. (위에 제목을 넣기 위함)
function getHeadCount(grdObj){
	var strConts = grdObj.Contents;	
	var iPos = pos(strConts,"<head>");
	var strHead = Mid(strConts,iPos);
	iPos = pos(strHead,"</head>");
	strHead = Mid(strHead,0,iPos+7);
	var spObj = split(strHead,chr(10),"webstyle");
	return spObj.length()-2;
}
