#include "LIB::cust.js"; // 공통업무 관련
var REPORTGUBUN = "";
function Form_OnLoadCompleted(obj)
{
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");  
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.  
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

	gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
	//gfn_SearchCustomerCourse(ds_oCourse, "Y", ""); // 대표고객직능 조회
	gfn_SearchCustomerCourseFullName(ds_ioCourse, "0","선택");	// 대표고객직능 조회
	radCourse.Value = "";
	//gfn_SearchCustomerDetailCourse("대표직능", ds_oDetailCourse, "Y", ""); // 세부고객직능 조회
    cbxCourse.index=0;
	cbxCourse1.index=0;      
	cbxCourse2.index=0;
	cbxCourse3.index=0; 
	cbxCourse4.index=0;
	cbxCourse5.index=0;
	cbxCourse6.index=0; 
	
	gfn_SearchCommonCode(ds_oAdditionInfo, "BIZCD", "Y");
	cbxBusinessType1.Index = "0"; 
	cbxBusinessType2.Index = ds_oAdditionInfo.rowcount - 1;
    
    //인력구분
    cbxCpersonGubun.Value="";
	cbxPersonD1.Value="";
	cbxPersonD2.Value="";
	cbxPersonD3.Value="";
	cbxPersonD4.Value="";
	cbxPersonD5.Value="";
	cbxPersonD6.Value="";
    
    gdList1.Visible = true;
    gdList2.Visible = false;
    gdList3.Visible = false;
    gdList4.Visible = false;
    gdList5.Visible = false;
    gdList6.Visible = false;
    
    var vTop = 275;
    gdList1.Top = vTop;
    gdList2.Top = vTop;
    gdList3.Top = vTop;
    gdList4.Top = vTop;
    gdList5.Top = vTop;
    gdList6.Top = vTop;
    var vLeft = 8;
    gdList1.Left = vLeft;
    gdList2.Left = vLeft;
    gdList3.Left = vLeft;
    gdList4.Left = vLeft;
    gdList5.Left = vLeft;
    gdList6.Left = vLeft;
    
    var vWidth = 815;
    gdList1.Width = vWidth ;
    gdList2.Width = vWidth ;
    gdList3.Width = vWidth ;
    gdList4.Width = vWidth ;
    gdList5.Width = vWidth ;
    gdList6.Width = vWidth ;
    
    var vHeight = 305;
    gdList1.Height = vHeight ;
    gdList2.Height = vHeight ;    
    gdList3.Height = vHeight ;
    gdList4.Height = vHeight ;
    gdList5.Height = vHeight ;
    gdList6.Height = vHeight ;
}

