function lfn_Search(obj) {
	if(radReport.Value == "1" || radReport.Value == "2"){
		CourseSearch();
	}	
	else if(radReport.Value == "3" || radReport.Value == "4"){	
		BizSearch();
	}
	else if(radReport.Value == "5" || radReport.Value == "6"){	
		ManagerSearch();
	}	
}

// 직능별 
function CourseSearch(){
	tit_clearActionInfo(this);
    if(radCourse.Value == "1"){
        if(cbxCourse.index == "0"){
            alert("직능을 선택하십시오.");
            return false;
        }
    }
    if(radCourse.Value == "2"){
        if(cbxCourse1.index == "0" &&
           cbxCourse2.index == "0" &&
           cbxCourse3.index == "0" &&
           cbxCourse4.index == "0" &&
           cbxCourse5.index == "0" &&
           cbxCourse6.index == "0"
        ){
            alert("직능을 선택하십시오.");
            return false;
        }        
    }
    
    if(radCpersonGubun.Value == "2"){
        if(cbxPersonD1.index == "0" &&
           cbxPersonD2.index == "0" &&
           cbxPersonD3.index == "0" &&
           cbxPersonD4.index == "0" &&
           cbxPersonD5.index == "0" &&
           cbxPersonD6.index == "0"
        ){
            alert("인력구분을 선택하십시오.");
