    }
	//TAB_Training.TabIndex = 1; //조회버튼 선택시  나오게 한다.
		tit_addSearchActionInfo("cust:searchCust0150CourseStatistics", false);
	tit_addSearchActionInfo("cust:searchCust0150CourseStatisticsNotMember1", false);
	tit_addSearchActionInfo("cust:searchCust0150CourseStatisticsNotMember2", false);	tit_addSearchActionInfo("cust:searchCust0150CourseStatisticsTotal", false);
	
	ds_SearchJogeon.DeleteAll();		ds_SearchJogeon.AddRow();
	
	ds_SearchJogeon.setColumn(0, "REPORT_GUBUN", radReport.Value);	
	ds_SearchJogeon.setColumn(0, "JIBU", cbxJibu.Value);	
	ds_SearchJogeon.setColumn(0, "MEMBER_GUBUN", radMemberGubun.Value);	
	ds_SearchJogeon.setColumn(0, "SELECT_COURSE", radCourse.Value);	
	ds_SearchJogeon.setColumn(0, "COURSE_GUBUN", cbxCourse.Value);	
	
	var Course = "";
	
	if(cbxCourse1.Value != "")
		Course += "'" + cbxCourse1.Value + "'" + ",";
	if(cbxCourse2.Value != "")
		Course += "'" + cbxCourse2.Value + "'" + ",";	
    if(cbxCourse3.Value != "")
		Course += "'" + cbxCourse3.Value + "'" + ",";	
	if(cbxCourse4.Value != "")
		Course += "'" + cbxCourse4.Value + "'" + ",";	
	if(cbxCourse5.Value != "")
		Course += "'" + cbxCourse5.Value + "'" + ",";	
	if(cbxCourse6.Value != "")
		Course += "'" + cbxCourse6.Value + "'" + ",";	
		
	ds_SearchJogeon.setColumn(0, "COURSE_GUBUN_IN", left(Course, length(Course)-1));
	
	ds_SearchJogeon.setColumn(0, "SELECT_BIZ", radBusinessType.Value);	
	ds_SearchJogeon.setColumn(0, "BIZ_GUBUN1", cbxBusinessType1.Value);	
	ds_SearchJogeon.setColumn(0, "BIZ_GUBUN2", cbxBusinessType2.Value);	
	
	// 인력구분
	ds_SearchJogeon.setColumn(0, "SELECT_PERSON", radCpersonGubun.Value);	
	ds_SearchJogeon.setColumn(0, "PERSON_GUBUN", cbxCpersonGubun.Value);
	
	var vPerson = "";
	
	if(cbxPersonD1.Value != "")
		vPerson += "'" + cbxPersonD1.Value + "'" + ",";
	if(cbxPersonD2.Value != "")
		vPerson += "'" + cbxPersonD2.Value + "'" + ",";	
    if(cbxPersonD3.Value != "")
		vPerson += "'" + cbxPersonD3.Value + "'" + ",";	
	if(cbxPersonD4.Value != "")
		vPerson += "'" + cbxPersonD4.Value + "'" + ",";	
	if(cbxPersonD5.Value != "")
		vPerson += "'" + cbxPersonD5.Value + "'" + ",";	
	if(cbxPersonD6.Value != "")
		vPerson += "'" + cbxPersonD6.Value + "'" + ",";	
		
	ds_SearchJogeon.setColumn(0, "PERSON_GUBUN_IN", left(vPerson, length(vPerson)-1));
	
	ds_SearchJogeon.setColumn(0, "BOOK1", radBook1.Value);	// 소방안전지 수령여부
	ds_SearchJogeon.setColumn(0, "BOOK2", radBook2.Value);	// 소방기술지 수령여부

	tit_callService(  
