	               , "ds_iCourseStatistics=ds_SearchJogeon"//inDs 
	               , "ds_CourseStatistics=ds_oCourseStatistics"
	                 +" ds_CourseStatisticsNotMember1=ds_oCourseStatisticsNotMember1" 
	                 +" ds_CourseStatisticsNotMember2=ds_oCourseStatistics2" //outDs
	                 +" ds_CourseStatisticsTotal=ds_oCourseStatisticsTotal" 
	               , ""
	               , "afterSearch_Course"  //callBack
	               , false
	               ); 
}

// 조회 후 callbackfunction afterSearch_Course(errCode, errMsg){

	if(errCode != 0 ){
		alert("오류가 발생했습니다.");
	}
	else{
		if(ds_CourseStatistics.rowcount > 0){
			var member = 0;
			var nmember = 0;
			var cover = 0;
			
			for(var i=0 ; i < ds_CourseStatistics.rowcount ; i++)
			{
				member += toNumber(ds_CourseStatistics.GetColumn(i,"MEMBER_CNT"));
				nmember += toNumber(ds_CourseStatistics.GetColumn(i,"NMEMBER_CNT"));
				cover += toNumber(ds_CourseStatistics.GetColumn(i,"COVER_CNT"));
			}
			medMemberCnt.Text = member;
			medNMemberCnt.Text = nmember;
			medCoverCnt.Text = cover;
			medTotalCnt.Text = member+nmember;
			REPORTGUBUN = radCourse.Value;
			if(REPORTGUBUN == "1" || REPORTGUBUN == "2"){
                gdList1.Format = "format2";
                gdList2.Format = "format2";
			}
			else{
                gdList1.Format = "Default";
                gdList2.Format = "Default";
			}
		}
		else{
			medMemberCnt.Text = "";
			medNMemberCnt.Text = "";
			medCoverCnt.Text = "";
			medTotalCnt.Text = "";
            REPORTGUBUN = "";
		}
	}}

