//엑샐인쇄 시작----------------------------------------------------------------------------------------
function lfn_ExcelL(obj)
{	
	if(radReport.Value == "1" || radReport.Value == "2"){
		if(gfn_IsNull(radMemberGubun.Value)|| radMemberGubun.Value == "0"){
			if(ds_CourseStatistics.rowcount == 0){
				alert("데이터가 없습니다.");
				return;
			}
		}
		else if(radMemberGubun.Value == "1"){
			if(ds_CourseStatisticsNotMember1.rowcount == 0 && ds_CourseStatisticsNotMember2.rowcount == 0){
				alert("데이터가 없습니다.");
				return;
			}
		}
		if(radReport.Value == "1"){
			var strText = "고객 선임자 현황";
             setExcelGridHeader(gdList1,strText);
		}
		else if(radReport.Value == "2"){
			var strText = "고객 선임자 현황";
			setExcelGridHeader(gdList1,strText);
		}
	}
	else if (radReport.Value == "3" || radReport.Value == "4"){
		if(ds_BizStatistics.rowcount == 0){
				alert("데이터가 없습니다.");
				return;
		}
		if(radReport.Value == "3"){
			var strText = "고객 선임자 현황";
			setExcelGridHeader(gdList3,strText);	
		}
		else if(radReport.Value == "4"){
			var strText = "고객 선임자 현황";
			setExcelGridHeader(gdList4,strText);	
		}
	}
	else if (radReport.Value == "5" || radReport.Value == "6"){
		if(ds_ManagerStatistics.rowcount == 0){
				alert("데이터가 없습니다.");
				return;
		}
		if(radReport.Value == "5"){
			var strText = "고객 선임자 현황";
			setExcelGridHeader(gdList5,strText);	
		}
		else if(radReport.Value == "6"){
			var strText = "고객 선임자 현황";
			setExcelGridHeader(gdList6,strText);	
		}
	}	
}
//엑셀 함수 입니다. (위에 제목을 넣기 위함)
function setExcelGridHeader(grdObj,strText){
	create("grid","grdExcel",'BindDataSet="' + grdObj.BindDataset +'"');
	grdExcel.contents = grdObj.Contents;	
	var headCnt = getHeadCount(grdExcel);
	var totCol  = grdExcel.GetColCount();
	var irow;
	for(var i = 0 ; i < headCnt ; i++ )	{
		irow = toInteger(grdExcel.GetCellProp("head",i,"row"));
		grdExcel.SetCellProp("head",i,"row",irow+1);
	}
	var str_temp = grdExcel.GetCurContents();
	var str_Head = '<cell align="left" col="0" colspan="' + totCol + '" display="text" text="' + strText + '"/>';	
	str_temp = replace(str_temp, '</head>',str_Head +'</head>');
	grdExcel.contents = str_temp;
	grdExcel.ExportExcelEx("sheet1","A1",false,true,true);
	Destroy(grdExcel);
}
//엑셀 함수 입니다. (위에 제목을 넣기 위함)
function getHeadCount(grdObj){
	var strConts = grdObj.Contents;	
	var iPos = pos(strConts,"<head>");
	var strHead = Mid(strConts,iPos);
	iPos = pos(strHead,"</head>");
	strHead = Mid(strHead,0,iPos+7);
	var spObj = split(strHead,chr(10),"webstyle");
	return spObj.length()-2;
}
