function lfn_Print(obj)
{
	if(radReport.Value == "1" || radReport.Value == "2"){
		if(ds_CourseStatistics.rowcount <= 0 && ds_CourseStatisticsNotMember1.rowcount <= 0 && ds_CourseStatisticsNotMember2.rowcount <= 0){
			alert("출력 할 데이터가 없습니다.");
			return;
		}

		gfn_CallReportPopup("frmCust0150R01CourseStatistics.rex"
                      , "ds_CourseStatistics||ds_CourseStatistics||ds_CourseStatisticsNotMember1||ds_CourseStatisticsNotMember2"
                      , "REPORT_GUBUN=" + radReport.Value + "^MEMBER_GUBUN=" + radMemberGubun.Value + "^COURSE_GUBUN=" + REPORTGUBUN
                      , true);
	}	
	else if(radReport.Value == "3" || radReport.Value == "4"){	
		if(ds_BizStatistics.rowcount <= 0){
			alert("출력 할 데이터가 없습니다.");
			return;
		}
		gfn_CallReportPopup("frmCust0150R02BizStatistics.rex"
                      , "ds_BizStatistics"
                      , "REPORT_GUBUN=" + radReport.Value
                      , true);
	}
	else if(radReport.Value == "5" || radReport.Value == "6"){	
		if(ds_ManagerStatistics.rowcount <= 0){
			alert("출력 할 데이터가 없습니다.");
			return;
		}
		gfn_CallReportPopup("frmCust0150R03ManagerStatistics.rex"
                      , "ds_ManagerStatistics"
                      , "REPORT_GUBUN=" + radReport.Value
                      , true);
	}
	
}

