function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if (gfn_IsNull(strCode)) {	
		radCpersonGubun.Index = 0;
		radCpersonGubun.Enable = false;
		lfn_FilterPersonGubun(strCode); 
    } else {
        radCpersonGubun.Index = 1;
        radCpersonGubun.Enable = true;
        lfn_FilterPersonGubun(strCode);
    }
    radCpersonGubun_OnChanged(object("radCpersonGubun"),radCpersonGubun.Value);
}


/*
 * 인력구분 필터링
 */
function lfn_FilterPersonGubun(sFilterGubun) {
	
	if(gfn_IsNull(sFilterGubun)) {
        sFilterGubun = 99;
    }
	
	ds_oPersonGubun.UnFilter();
	var fGubun;		//필터구분(직능)
	switch(sFilterGubun)
	{
		//공공기관
		case 13 :
			fGubun = "10";
		break;
		case 14 :
			fGubun = "10";
		break;
		//위험물
		case 30 :
			fGubun = "30";
		break;
		//소방기술자
		case 50 :
			fGubun = "50";
		break;
		//개인회원
		case 60 :
			fGubun = "60";
		break;
        default :
            fGubun = "99";
        break;
	}
	
	var ls_FilterExpr = "";
    if (!gfn_IsNull(fGubun)) {
        ls_FilterExpr += "PCD=='' || PCD=='" + trim(fGubun) + "'";
    }
    ds_oPersonGubun.Filter(ls_FilterExpr);
    
    if(fGubun == "99") {
        radCpersonGubun.Index = 0;
        radCpersonGubun.Enable = false;
        radCpersonGubun_OnChanged(object("radCpersonGubun"),radCpersonGubun.Value);
    }
}

//인력구분
function radCpersonGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if (strCode=="1") {				// 선택
        cbxCpersonGubun.Index=0;
        cbxCpersonGubun.Enable=true;
        cbxCpersonGubun.SetFocus();

        cbxPersonD1.Enable = false;
        cbxPersonD2.Enable = false;
        cbxPersonD3.Enable = false;
        cbxPersonD4.Enable = false;
        cbxPersonD5.Enable = false;
        cbxPersonD6.Enable = false;        

        cbxPersonD1.Index=0;
        cbxPersonD2.Index=0;
        cbxPersonD3.Index=0;
        cbxPersonD4.Index=0;
        cbxPersonD5.Index=0;
        cbxPersonD6.Index=0;
        
	} else if (strCode=="2") {		// 다중선택
        
        cbxCpersonGubun.Value = "";
        cbxCpersonGubun.Enable=false;
        cbxPersonD1.Enable = true;
        cbxPersonD2.Enable = true;
        cbxPersonD3.Enable = true;
        cbxPersonD4.Enable = true;
        cbxPersonD5.Enable = true;
        cbxPersonD6.Enable = true;        

        cbxPersonD1.Index=0;
        cbxPersonD2.Index=0;
        cbxPersonD3.Index=0;
        cbxPersonD4.Index=0;
        cbxPersonD5.Index=0;
        cbxPersonD6.Index=0;
        cbxPersonD1.SetFocus();
	} else {						// 전체
        cbxCpersonGubun.Value = "";
        cbxCpersonGubun.Enable=false;
        
        cbxPersonD1.Enable = false;
        cbxPersonD2.Enable = false;
        cbxPersonD3.Enable = false;
        cbxPersonD4.Enable = false;
        cbxPersonD5.Enable = false;
        cbxPersonD6.Enable = false;        

        cbxPersonD1.Index=0;
        cbxPersonD2.Index=0;
        cbxPersonD3.Index=0;
        cbxPersonD4.Index=0;
        cbxPersonD5.Index=0;
        cbxPersonD6.Index=0;

	}

}
