/* Form Size조정에 대한 보정처리 */
function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdAgreeList.Height = nCy - gdAgreeList.Top - 8;
        gdAgreeList.Width = nCx - gdAgreeList.Left - 8;
        Shape1.Height = nCy - Shape1.Top - 4;
        Shape1.Width = nCx - Shape1.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

    ds_oAgreeList.ClearData(); // 데이타셋 초기화

	gdAgreeList.Redraw = false;
	
    tit_clearActionInfo();    

	tit_addCsvSearchActionInfo("cust:searchCust0210MobileAgree", false);
	
	var tmpEdIsHp = Replace(edIsHp.Text, "-", "");
	
	//파라미터(quote)로 처리하지 않고 DataSet으로 처리한 샘플.
	//직접 바인드로 처리하면 더 줄어들겠지만, 예를 들기 위해 아래와 같이 처리했음.
	ds_iAgreeList.ClearData();
	ds_iAgreeList.AddRow();
	ds_iAgreeList.SetColumn(0, "SJIBU", 	cbxJibu.Value);
	ds_iAgreeList.SetColumn(0, "SCOURSE", 	substr(cbxCourse.Value,0,1));
	ds_iAgreeList.SetColumn(0, "SFMNM", 	edPersonNm.Text);
	ds_iAgreeList.SetColumn(0, "SISHP", 	tmpEdIsHp);

	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iAgreeList=ds_iAgreeList"	// inDs
		,"ds_oAgreeList=ds_oAgreeList"	// outDs
		/*,"SJIBU=" + quote(cbxJibu.Value)
		+ " SCOURSE=" + quote(substr(cbxCourse.Value,0,1))
		+ " SFMNM=" + quote(edPersonNm.Text)
		+ " SISHP=" + quote(tmpEdIsHp)	// arg*/
		,""
		,"lfn_AfterSearchAgreeManagement"
		,false
	);
}

/*
 * 기능 : 조회
 */
function lfn_Save(obj)
{
	if (ds_oAgreeList.GetCount() == 0) {
	
		gfn_Alert("변경할 내역이 없습니다.");
		return;	
	}
	
	if (ds_oAgreeList.GetUpdate()==false) {	
		gfn_Alert("변경된 내역이 없습니다.");
		return;	
	}	
	
	if(!gfn_Confirm("변경내역을 저장하시겠습니까?")){
		return;
	}
	
	
	
	// dataset  
    for(var i=0; i<ds_oAgreeList.GetRowCount(); i++) {
    
		if(ds_oAgreeList.GetRowType(i)!="update") continue;
		if (!gfn_IsNull(ds_oAgreeList.GetColumn(i, "ISMGNO"))) {

			tit_clearActionInfo();
			
			iArgsNm = "ISMGNO=" + quote(ds_oAgreeList.GetColumn(i, "ISMGNO"));
			
			tit_addSingleActionInfo("cust:updateCust0210MobileAgree", "", 0, "");  // 저장
			
			// 서버 호출 
			tit_callService(
				""
				,""
				,""		// inDs
				,""      // outDs
				,iArgsNm	// args
				,"lfn_AfterUpdate"
				,""
				,""
				,true
			);			
		
		}
	} // for
	
	lfn_Search();
}

/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchAgreeManagement(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	if(ds_oAgreeList.GetRowCount() > 0) {
		medTotalCnt.Value = ds_oAgreeList.GetRowCount();
		/*switch(radOrder.Value)
		{
			case 0 :
				ds_oPostReturn.Sort("PRPDDATE:D,CNO:A");
			break;

			case 1 :
				ds_oPostReturn.Sort("CNO:A,PRPDDATE:D");
			break;

			case 2 :
				ds_oPostReturn.Sort("GTPRTSEQ:A,PRPDDATE:D,CNO:A");
			break;
		} */
	} else {
		medTotalCnt.Value = 0;
	}
	gdAgreeList.Redraw = true;
    gfn_SearchMessage(gdAgreeList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

//저장완료후 호출
function lfn_AfterUpdate(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
}

