// Excel Export(F11)
function btnToExcel_OnClick(obj) {
    if (ds_oAgreeList.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
    }
	// 개인정보 다운로드 사유 등록  
	var parValue = "sCode=1462";	// COMMON.PERSONALDOWNLOADH에 등록된다. 신규화면은 COMMON.PERSONALCODE에 DB로 등록해서 사용해야 함.
		parValue += " sRowCnt=" + ds_oAgreeList.GetRowCount();
		parValue += " sTitle=" + quote(gdAgreeExcelList.UserData);
	var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	
	gdAgreeExcelList.ExportExcel(sFormTitle, false, true);     
	//lfn_Excel(obj);
}

