// 초기화(단축키로 하면 공통에서 처리하고, 버튼으로 처리하면 로컬 function으로 처리. 공통 초기화는 초기세팅값을 날려버림)
function lfn_LocalCancel(obj)
{
	cbxJibu.Index	= c_cbxJibu;			// 지부 콤보 세팅
	cbxCourse.Index = 1;					// 직능 콤보 세팅
	edPersonNm.Text	= c_edPersonNm;			// 성명
	edIsHp.Text		= c_edIsHp;				// 연락처
	
	ds_oAgreeList.ClearData();
}
